import React from 'react';
import {
    Box,
    RadioGroup,
    Stack,
    Radio
  } from '@chakra-ui/react';



export const OrderOptions = ({onDeliveryOptionChange,onPaymentOptionChange, deliveryValue,  paymentValue, deliverOptions, paymentOptions}) => {
    return (
      <Box display='flex' flexWrap='wrap' my={4} justifyContent='space-between'>
        <SingleOrderOption onChange={onDeliveryOptionChange} value={deliveryValue} orderOption={deliverOptions}/>
        <SingleOrderOption onChange={onPaymentOptionChange} value={paymentValue} orderOption={paymentOptions}/>
      </Box>
    )
  }
  
  const SingleOrderOption = ({ orderOption, onChange, value}) => {
    return (
      <Box
        width={{base: '100%', lg: '48%'}}
        border='1px solid'
        borderColor='gray.300'
        borderRadius={5}
        p={2}
        marginBottom={{base: '15px', lg: '0px'}}
      >
        <Box
          mb={3}
          textStyle='label'
          fontSize={{ base: 'sm', lg: 'md' }}
          layerStyle='muted'
        >
          {orderOption.title}
        </Box>
        <RadioGroup onChange={onChange} defaultValue={value} value={value} >
          <Stack spacing={10} direction='row'>
            {orderOption.options.map((item) => (
              <Radio key={item} colorScheme='blue' value={item} >
                {item}
              </Radio>
            ))}
          </Stack>
        </RadioGroup>
      </Box>
    );
  };
