import React, { useState } from 'react';
// import PropTypes from 'prop-types';
import classnames from 'classnames';
import { NavLink } from 'react-router-dom';
import logo from '../../images/logo.png';
import { FormattedMessage, useIntl } from 'react-intl';
import messages from './messages';
import parts from '../../images/parts.png';
import get from 'lodash/get';

import { useDispatch, useSelector } from 'react-redux';
import { Select } from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';
//import {FiList} from 'react-icons/fi';
import { FaBuffer } from 'react-icons/fa';
import { Box, useColorMode } from '@chakra-ui/react';

import { attemptLogout } from '../../containers/LoginPage/actions';
import { changeLocale } from '../../containers/LanguageProvider/actions';
import { BsColumns, BsCart3 } from 'react-icons/bs';
import { IoStatsChart } from 'react-icons/io5';
import { MdAccountBalanceWallet, MdListAlt } from 'react-icons/md';
import { BiTransfer } from 'react-icons/bi';
import { GenericGroupNav } from './NewSideBar';
import { FiTrendingDown } from 'react-icons/fi';
import { AiOutlineUngroup } from 'react-icons/ai';
import { selectGlobalState } from '../../globalSlice';
import { UseOnboardContext } from '../../App'
import { RiCaravanLine } from 'react-icons/ri';

const Sidebar = () => {
  const { isDistributor } = useSelector(selectGlobalState);
  const { onboard } = UseOnboardContext()
  const { colorMode } = useColorMode();
  const [sidebarShow, setSidebarShow] = useState('-translate-x-full');
  const dispatch = useDispatch();
  const history = useHistory();
  const storeLocale = useSelector((state) => get(state, 'language.locale', ''));
  const handleLanguageChange = (event) => {
    dispatch(changeLocale(event.target.value));
  };
  const intl = useIntl();

  const navObjs = [
    {
      to: '/dashboard',
      icon: 'fas fa-warehouse',
      message: messages.home,
    },
    {
      to: '/distributor',
      icon: <BsColumns className='mr-2 text-sm' />,
      message: messages.distributor,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employee', icon: 'fas fa-users', message: messages.employee },
    /*   { to: "/part/add", icon: 'fas fa-plus', message: messages.part }, */
    {
      to: '/parts',
      icon: (
        <img
          src={parts}
          alt='parts'
          className='max-w-full rounded '
          width={20}
          height={20}
          style={{ alignSelf: 'center', marginRight: '4px' }}
        />
      ),
      message: messages.parts,
    },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
    {
      to: '/sales',
      icon: <IoStatsChart className='mr-2 text-sm' />,
      message: messages.sales,
    },
  ];

  const orderNavs = {
    mainNav: 'Orders',
    to: '/orders',
    icon: <FaBuffer className='mr-2 text-sm' />,
    subNavs: [
      { name: messages.placeOrder, to: '/place-order', icon: <BsCart3 className='mr-2 text-sm'  /> },
    ],
  };

  const accountNavs = {
    mainNav: 'Account',
    to: '/accounts',
    icon: <MdAccountBalanceWallet  className='mr-2 text-sm' />,
    subNavs: [
      {
        name: messages.outstanding,
        to: '/outstanding',
        icon: <FiTrendingDown className='mr-2 text-sm'  />,
      },
      {
        name: messages.collections,
        to: '/collections',
        icon: <AiOutlineUngroup className='mr-2 text-sm'  />,
      },
    ],
  };

  const inventoryNavs = {
    mainNav: 'Inventory',
    to: '/inventory',
    icon: <MdListAlt className='mr-2 text-sm'  />,
    subNavs: [
      {
        name: messages.stockTransfer,
        to: '/stock-transfer',
        icon: <BiTransfer className='mr-2 text-sm'  />,
      },
    ],
  };

  const navObjs2 = [
    {
      to: '/distributor',
      icon: <BsColumns className='mr-2 text-sm' />,
      message: messages.distributor,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employees', icon: 'fas fa-users', message: messages.employee },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
  ];

  const employeeNav = [
    {
      to: '/dashboard',
      icon: 'fas fa-warehouse',
      message: messages.home,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employees', icon: 'fas fa-users', message: messages.employee },
    {
      to: '/parts',
      icon: <RiCaravanLine className='mr-2 text-sm'  />,
      message: messages.parts,
    },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
    {
      to: '/sales',
      icon: <IoStatsChart className='mr-2 text-sm'  />,
      message: messages.sales,
    },
  ];

  return (
    <>
      <Box
        as='nav'
        backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
        className={`block py-4 px-6 overflow-y-auto top-0 bottom-0 w-64 bg-white shadow-xl left-0 fixed flex-row flex-nowrap md:z-10 z-9999 transition-all duration-300 ease-in-out transform md:translate-x-0
          ${sidebarShow}`}
      >

        <button
          className='md:hidden flex items-center justify-center cursor-pointer text-blueGray-700 w-8 h-10 border-l-0 border-r border-t border-b border-solid border-blueGray-100 text-xl leading-none bg-white rounded-r border border-solid border-transparent absolute -right-24-px focus:outline-none z-9998'
          style={{
            right: '-69px',
            width: '3.5rem',
            borderRadius: '3px',
            position: 'fixed',
          }}
          type='button'
          onClick={() => {
            if (sidebarShow === '') {
              setSidebarShow('-translate-x-full');
            } else {
              setSidebarShow('');
            }
          }}
        >
          <i className='fas fa-bars' />
        </button>
        <Box
          backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
          className='flex-col min-h-full px-0 flex flex-wrap items-center justify-between w-full mx-auto overflow-y-auto overflow-x-hidden'
        >
          <Box
            backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
            className='flex bg-white flex-col items-stretch opacity-100 relative mt-4 overflow-y-auto overflow-x-hidden h-auto z-40 items-center flex-1 rounded w-full'
          >
            <div className='flex justify-center items-center'>
              <NavLink
                className='w-20 h-20 rounded-full flex justify-center'
                to={(isDistributor && !localStorage.onboard) || !isDistributor? '/dashboard' : '/distributor'}
              >
                <img src={logo} alt='Logo' className='max-w-full rounded' />
              </NavLink>
            </div>
            <span className='text-center text-lg font-bold mt-4'>
              <FormattedMessage {...messages.garaazAdmin} />
            </span>
            <div className='md:flex-col md:min-w-full flex flex-col list-none'>
              <hr className='my-4 md:min-w-full' />
              <h6 className='md:min-w-full text-blueGray-500 text-xs uppercase font-bold block pt-1 pb-4 no-underline'>
                <FormattedMessage {...messages.distributor} />
              </h6>
             {onboard && isDistributor ? navObjs2.map((item) => {
                    return (
                      <SingleNav
                        key={item.to}
                        to={item.to}
                        icon={item.icon}
                        message={item.message}
                      />
                    );
                  }) : (isDistributor?  navObjs : employeeNav).map((item) => {
                    return (
                      <SingleNav
                        key={item.to}
                        to={item.to}
                        icon={item.icon}
                        message={item.message}
                      />
                    );
                  })}
              {((isDistributor && !localStorage.onboard) || !isDistributor) &&  <GenericGroupNav {...orderNavs} />}
              {((isDistributor && !localStorage.onboard) || !isDistributor) && <GenericGroupNav {...accountNavs} />}
              {((isDistributor && !localStorage.onboard) || !isDistributor) && <GenericGroupNav {...inventoryNavs} />}
            </div>

            <div
              style={{
                display: 'flex',
                flexDirection: 'column',
                marginTop: 'auto',
                alignItems: 'flex-start',
              }}
            >
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <i className='fas fa-language'></i>
                <Select value={storeLocale} onChange={handleLanguageChange}>
                  <option value='en'>Eng</option>
                  <option value='hi'>
                    {' '}
                    {intl.formatMessage(messages.hindi)}
                  </option>
                </Select>
              </div>

              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <i className='fas fa-sign-out-alt mr-3'></i>
                <NavLink
                  onClick={() => {
                    dispatch(attemptLogout());
                    // history.push('/login')
                  }}
                  exact
                  to='/login'
                  className={classnames(
                    'text-xs uppercase py-3 font-bold block',
                  )}
                  activeClassName='text-red-500 hover:text-red-700'
                  style={{ display: 'flex', marginLeft: '5px' }}
                >
                  <FormattedMessage {...messages.logout} />
                </NavLink>
              </div>
            </div>
          </Box>
        </Box>
      </Box>
    </>
  );
};

const SingleNav = ({ to, icon, message }) => {
  return (
    <NavLink
      exact
      to={to}
      className={classnames('text-xs pl-4 uppercase py-3 font-bold block flex')}
      activeClassName='text-red-500 hover:text-red-700'
    >
      {typeof icon === 'string' ? (
        <i className={classnames(icon, 'mr-2 text-sm')} />
      ) : (
        icon
      )}

      <FormattedMessage {...message} />
    </NavLink>
  );
};

export default Sidebar;
