import { Button } from '@chakra-ui/react';
import { useIntl } from 'react-intl';
import { Box } from '@chakra-ui/react';
const FormactionButtons = ({ saveLabel, onReset, cancleLabel, isLoading }) => {
  const intl = useIntl()
  return (
   /*  <div className="w-full lg:w-6/12" style={{ height: "100%" }}> */
      <Box display='flex' justifyContent='flex-end'>
        <Button type="submit" variant="normal" mr={3}  isLoading={isLoading}
          loadingText='Submitting'>
         {intl.formatMessage(saveLabel)}
        </Button>

        <Button
          type="button"
          onClick={onReset}
          color="light"
         
        >
          {intl.formatMessage(cancleLabel)}
        </Button>
      </Box>
  /*   </div> */
  );
}
export default FormactionButtons;
