import { publicAxios as axios } from '../../axios';
import routes from '../../routes';
import getURLParams from '../../utils/getURLParams';

export const createBulkAccount = (payload) => {
    return axios({
        method: 'POST',
        url: `${routes.accountRoute}`,
        data: payload.data,
        headers: {
            Authorization: payload.auth.authToken,
        }
    })
}

export const fetchAllAccounts = (payload) => {
    const params = getURLParams(payload.params)
    return axios({
        method: 'GET',
        url: `${routes.accountRoute}${params}`,
        headers: {
            Authorization: payload.auth.authToken,
        }
    })
}

export const fetchWorkshopAccounts = (payload) => {
    const params = getURLParams(payload.params)
    return axios({
        method: 'GET',
        url: `${routes.workshopRoute}/${payload.id}/accounts${params}`,
        headers: {
            Authorization: payload.auth.authToken,
        }
    })
}

export const uploadAccountMedia = (payload) => {
    return axios({
        method: 'POST',
        url: `/media`,
        headers: {
            Authorization: payload.auth.authToken,
        },
        data: payload.data,
    })
}


export const AddToQueue = (payload) => {
    return axios({
        method: 'POST',
        url: `${routes.uploadQueueEndpoint}`,
        headers: {
            Authorization: payload.auth.authToken,
        },
        data: payload.data,
  
    })
}

export const getFileQueue = (payload) => {
    return axios({
        method: 'GET',
        url: `${routes.uploadQueueEndpoint}?module=ACCOUNT`,
        headers: {
            Authorization: payload.auth.authToken,
        },

    })
}
