import React from "react";
import { Tr, Td, Button } from "@chakra-ui/react"

const Employee = ({ employee, setPosition, setOpenActionPop, setSelectedEmployee }) => {
	const { accountStatus = "", userProfile = [], branch = [] } = employee
	//const { branchCode = "", branchName = "" } = branch[0];
	const {
		firstName = '',
		lastName = '',
		mobileNumber = '',
		designation = '',
	} = userProfile[0]
	return (
		<Tr >
			<Td>{accountStatus}</Td>
			<Td>{firstName + " " + lastName}</Td>
			<Td>{mobileNumber}</Td>
			<Td>{branch[0]?.branchCode}</Td>
			<Td>{branch[0]?.branchName}</Td>
			<Td>{designation}</Td>
			<Td>
				<Button bg="transparent" onClick={(e) => {
					const position = e.target.getBoundingClientRect();
					setSelectedEmployee(employee)
					setPosition({ top: position.top - 113, right: 130 })
					setOpenActionPop((prev) => !prev)
				}} ><i className="fas fa-ellipsis-h"></i></Button>

			</Td>
		</Tr>
	)
}
export default Employee;
