import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { useIntl } from 'react-intl';
import toast from 'react-hot-toast';
import get from 'lodash/get';
import {toUpper, isEmpty} from 'lodash';
import messages from './messages';
import FormactionButtons from '../../utils/FormactionButtons';
import { Box } from '@chakra-ui/react';
import InputScaffold from '../../utils/InputScaffold';

const WorkshopDetailsForm = ({ currentWorkshop, onFormSubmit }) => {
  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
    setValue,
    watch
  } = useForm({ mode: 'onSubmit' });
  const [workshopDetails, setWorkshopDetails] = useState({});
  const intl = useIntl();
  useEffect(() => {
    setWorkshopDetails(currentWorkshop);
  }, [currentWorkshop]);
  useEffect(() => {
    if (!isEmpty(workshopDetails)) {
      setValue('name', get(workshopDetails, 'name', ''));
      setValue('code', get(workshopDetails, 'code', ''));
      setValue('type', get(workshopDetails, 'type', ''));
      setValue('pan', get(workshopDetails, 'pan', ''));
      setValue('gstin', get(workshopDetails, 'gstin', ''));
    }
  }, [workshopDetails]);

  const handleFormSubmit = async (data) => {
    try {
      data.gstin = toUpper(data.gstin);
      onFormSubmit(data, 1);
    } catch (err) {
      toast(intl.formatMessage(messages.somethingWentWrong));
    }
  };
  const onReset = () =>
    reset(
      {},
      {
        keepDirty: false,
        keepErrors: false,
        keepValues: false,
      },
    );
  return (
    <form onSubmit={handleSubmit(handleFormSubmit)}>
      <div>
        <h6 className='mt-6 mb-2 font-bold'>
          {intl.formatMessage(messages.workshopDetails)}
        </h6>
        <hr className='mb-6 border-b-1 border-blueGray-200' />
        <Box display='flex' flexWrap='wrap' justifyContent='space-between'>
          <InputScaffold
            width='48%'
            label={messages.name}
            inputName='name'
            inputType='text'
            register={register}
            isRequired={true}
            errors={errors}
            requiredLabel={messages.workshopNameRequired}
          />
          <InputScaffold
            width='48%'
            label={messages.code}
            inputName='code'
            inputType='text'
            register={register}
            isRequired={true}
            errors={errors}
            requiredLabel={messages.workshopCodeRequired}
          />
        </Box>
        <Box display='flex' flexWrap='wrap' justifyContent='space-between' my={{base: 0, lg: 6}}>
          <InputScaffold
            width='48%'
            label={messages.type}
            inputName='type'
            inputType='text'
            register={register}
            isRequired={false}
            errors={errors}
            requiredLabel={messages.workshopTypeRequired}
          />
          <InputScaffold
            width='48%'
            label={messages.pan}
            inputName='pan'
            inputType='text'
            length={{ max: 10, min: 10 }}
            isRequired={false}
            errors={errors}
            requiredLabel={messages.panRequired}
            errMsg={messages.panInvalid}
            register={register}
          />
        </Box>
        <Box mb={6}>
        <InputScaffold
          width='48%'
          label={messages.gstin}
          inputName='gstin'
          inputType='text'
          isRequired={false}
          errors={errors}
          register={register}
          length={{max:15, min: watch('gstin')? 15 : 0}}
          requiredLabel={messages.gstRequired}
          errMsg={messages.gstInvalid}
          maxVal={15}
        />
        </Box>

        <FormactionButtons
          saveLabel={messages.saveButtonLabel}
          onReset={onReset}
          cancleLabel={messages.cancelButtonLabe}
        />
      </div>
    </form>
  );
};

WorkshopDetailsForm.propTypes = {
  onFormSubmit: PropTypes.func,
  currentWorkshop: PropTypes.object,
};

export default WorkshopDetailsForm;
