import {
  Table, Thead, Tbody, Tfoot, Tr, Th, Td, Flex, useDisclosure, Button, toast
} from "@chakra-ui/react"
import PropTypes from 'prop-types';
import { useState } from "react";
import Workshop from "./Workshop";
import { selectGlobalState } from '../../../globalSlice';
import '../style.css';
import { useSelector } from 'react-redux';

const WorkshopTable = ({ workshops }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const { isDistributor } = useSelector(selectGlobalState);

  return (

    <div style={{ width: "100%", overflow: "auto", marginTop: "20px" }}>
      <Table variant="simple" className="emp-table" style={{ width: "", overflow: "auto" }} >
        <Thead style={{ backgroundColor: "#1e293b", color: "#ffffff"}}>
          <Tr  height={{ base: '10vh', lg: '3rem' }}>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff" , wordBreak: 'keep-all'}}>Workshop Code</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Workshop Name</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff" , wordBreak: 'keep-all'}}>Workshop Type</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Branch Code</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Owner Name</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Mobile Number</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Credit Days</Th>
            <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff" , wordBreak: 'keep-all'}}>Discount</Th>
            {/* <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff" , wordBreak: 'keep-all'}}>Payment Terms</Th> */}
            {isDistributor && <Th style={{ color: "#ffffff", borderRight: "1px solid #ffffff", wordBreak: 'keep-all' }}>Action</Th>}
          </Tr>
        </Thead>
        <Tbody>
          {workshops.map((workshop) => {
            return (
              <Workshop
                isDistributor ={isDistributor}
                key={workshop._id}
                onOpen={onOpen}
                workshop={workshop}
              />
            )
          })}
        </Tbody>
        <Tfoot>
          <Tr>
            <Td>{workshops.length + (workshops.length > 1 ? " Workshops" : " Workshop")} </Td>
          </Tr>
        </Tfoot>
      </Table>
    </div >
  );
}
WorkshopTable.propTypes = {
  workshops: PropTypes.array
};
export default WorkshopTable;
