const stringifyArray = (value) => {
    return [...new Set(value)].reduce((prevState, currState) => {
      if (typeof currState === 'string') {
        return `${prevState}${prevState ? ',' : ''}${currState}`;
      }
      return `${prevState}${prevState ? ',' : ''}${currState.value}`;
    }, '');
  };

  export default stringifyArray;
