import { publicAxios as axios } from '../../axios';
import routes from './route';
import getURLParams from '../../utils/getURLParams';



class Services {
    static getAllStockTransferRequest(payload){
        const params = getURLParams(payload.params)
        return  axios({
            method: 'GET',
            url: `${routes.stockTransferEndpoint}${params}`,
            headers: {
                Authorization: payload.auth.authToken,
            }
        })
    }

    static getSingleStockTransferRequest(payload){
        return  axios({
            method: 'GET',
            url: `${routes.stockTransferEndpoint}/${payload.id}`,
            headers: {
                Authorization: payload.auth.authToken,
            }
        })
    }

    static updateStockTransferStatus(payload){
        /*   const params = getURLParams(payload.params) */
          return  axios({
              method: 'PATCH',
              url: `${routes.stockTransferEndpoint}/${payload.id}`,
              data: payload.data,
              headers: {
                  Authorization: payload.auth.authToken,
              }
          })
      }



   
}

export default Services;
