import React from 'react';
import PropTypes from 'prop-types';
import { Button } from '@chakra-ui/button';
import { FormattedMessage } from 'react-intl';
import messages from './messages';

const PreFooter = ({ type = 'light' }) => {
  const subtitleClass = 'leading-normal mt-1 mb-2';
  const wrapperClass = 'pt-4 pb-4 border-b';
  const wrapperTypes = {
    dark: 'bg-blueGray-800 border-blueGray-700',
    light: 'bg-blueGray-100',
  };
  const subtitleTypes = {
    dark: 'text-white',
    light: 'text-blueGray-500',
  };
  return (
    <>
      <div className={`${wrapperClass} ${wrapperTypes[type]}`}>
        <div className="container mx-auto px-4">
          <div className="justify-between items-center flex flex-wrap -mx-4">
            <div className="px-4 relative w-full lg:w-6/12">
              {/* <h3 className="text-xl font-bold leading-normal mt-0">
                <FormattedMessage {...messages.thankYouMessage} />
              </h3> */}
              <h4 className={`${subtitleClass} ${subtitleTypes[type]}`}>
                <FormattedMessage {...messages.getInTouchMessage} />
              </h4>
            </div>

            <div className="text-right pr-6 px-4 relative w-full lg:w-6/12">
              <Button color="white" outline={false} size="sm">
                <i className="fab fa-twitter" />
              </Button>
              <Button color="white" outline={false} size="sm">
                <i className="fab fa-facebook" />
              </Button>
              <Button color="white" outline={false} size="sm">
                <i className="fab fa-dribbble" />
              </Button>
              <Button color="white" outline={false} size="sm">
                <i className="fab fa-github" />
              </Button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

PreFooter.propTypes = {
  type: PropTypes.oneOf(['light', 'dark']),
};

export default PreFooter;
