import React from 'react';
import PropTypes from 'prop-types';
// components
import Sidebar from '../../components/Sidebar/NewSideBar';
import SmallSideBar from '../../components/Sidebar';
import NavbarAdmin from '../../components/NavbarAdmin';
import FooterAdmin from '../../components/FooterAdmin';
import {
  Box,
  useColorMode,
  useDisclosure,
  keyframes,
  IconButton,
} from '@chakra-ui/react';
import { AiFillCaretRight, AiFillCaretLeft } from 'react-icons/ai';

const close = keyframes`
  from { margin-left: 5vw; }
  to { margin-left: 15vw; }
`;

const open = keyframes`
  from { margin-left: 5vw; }
  to { margin-left: 5vw; }
`;

const toggleClose = keyframes`
  from { margin-left: 4vw; }
  to { margin-left: 14vw; }
`;

const toggleOpen = keyframes`
  from { margin-left: 4vw; }
  to { margin-left: 4vw; }
`;

const Layout = ({ title = '', children }) => {
  const { isOpen, onToggle } = useDisclosure({
    defaultIsOpen: localStorage.sideBarState ?? true,
  });
  const { colorMode } = useColorMode();
  const [innerWidth, setInnerWidth] = React.useState(window.innerWidth);

  React.useEffect(() => {
    window.addEventListener('load', () => {
      setInnerWidth(window.innerWidth);
    });

    return () =>
      window.removeEventListener('load', () => {
        setInnerWidth(window.innerWidth);
      });
  }, []);

  React.useEffect(() => {
    window.addEventListener('resize', () => {
      setInnerWidth(window.innerWidth);
    });

    return () =>
      window.removeEventListener('resize', () => {
        setInnerWidth(window.innerWidth);
      });
  }, []);

  const animateOpen = isOpen
    ? `${close} 0.3s linear 0.1s forwards`
    : `${open} 0s linear 0s forwards`;

  const animateToggle = isOpen
    ? `${toggleClose} 0.3s linear 0.1s forwards`
    : `${toggleOpen} 0s linear 0s forwards`;

  const handleSidebar = () => {
    localStorage.sideBarState = !isOpen;
    onToggle();
  };
  return (
    <Box position='relative'>
      {innerWidth > 768 ? (
        <Sidebar isOpen={isOpen} onToggle={onToggle} />
      ) : (
        <SmallSideBar />
      )}
      {innerWidth > 768 ? (
        <Box
          minHeight='100vh'
          backgroundColor={colorMode === 'dark' ? 'gray.900' : 'white'}
          animation={animateOpen}
          ml='5vw'
          className='relative pt-24'
        >
          <NavbarAdmin title={title} />
          <div
            className='px-4 md:px-5 mx-auto w-full mt-24'
            style={{
              height: '-webkit-fill-available',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
            }}
          >
            <Box minHeight='80vh'>{children}</Box>

            <FooterAdmin />
          </div>
        </Box>
      ) : (
        <Box
          backgroundColor={colorMode === 'dark' ? 'gray.900' : 'white'}
          className='relative pt-24 md:ml-64'
        >
          <NavbarAdmin title={title} />
          <div
            className='px-4 md:px-5 mx-auto w-full mt-24'
            style={{
              height: '-webkit-fill-available',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
            }}
          >
            {children}
            <FooterAdmin />
          </div>
        </Box>
      )}
      {innerWidth > 768 && (
        <IconButton
          backgroundColor={colorMode === 'dark' ? 'gray.900' : 'white'}
          animation={animateToggle}
          position='absolute'
          top='18px'
          zIndex='900'
          onClick={handleSidebar}
          p={0}
          size='xs'
          isRound={true}
          boxShadow='base'
        >
          {isOpen ? <AiFillCaretLeft /> : <AiFillCaretRight />}
        </IconButton>
      )}
    </Box>
  );
};

Layout.propTypes = {
  children: PropTypes.node,
  title: PropTypes.string,
};

export default Layout;
