import { SET_DISTRIBUTOR_INFO, GET_DISTRIBUTOR_INFO } from './constants';
import { publicAxios as axios } from '../../axios';

export const setDistributorInfo = (data) => {
  return {
    type: SET_DISTRIBUTOR_INFO,
    payload: data,
  };
}

export const getDistributorInfo = async (auth) => {
  const { data } = await axios.get(GET_DISTRIBUTOR_INFO
    , {
      headers: {
        Authorization: auth.authToken,
      },
    });
  return data[0] ? data[0] : {};
};
