import { SET__BRANCHES } from './constants';
import { publicAxios as axios } from '../../axios';
import { GET_ALL_ORDERS, SET_ALL_ORDERS, SET_TOTAL_COUNT } from './constants';
import getURLParams from '../../utils/getURLParams';
export function setBranches(data) {
  return {
    type: SET__BRANCHES,
    payload: data,
  };
}
// export const setTotalCount = (payload) => ({
//   type: SET_TOTAL_COUNT,
//   payload
// });
export const getBranchList = async (auth) => {
  const data = await axios.get('/branches', {
    headers: {
      Authorization: auth.authToken,
    },
  })
  // data.status = 404
  return data
}
