import get from 'lodash/get';

import { useDispatch, useSelector } from 'react-redux';
import { Select } from '@chakra-ui/react';
import { FaBuffer } from 'react-icons/fa';

import { attemptLogout } from '../../containers/LoginPage/actions';
import { changeLocale } from '../../containers/LanguageProvider/actions';
import { RiCaravanLine } from 'react-icons/ri';
import { BiTransfer } from 'react-icons/bi';
import { FiTrendingDown } from 'react-icons/fi';
import { AiOutlineUngroup } from 'react-icons/ai';
import { MdAccountBalanceWallet, MdListAlt } from 'react-icons/md';
import { UseOnboardContext } from '../../App'
import { IoStatsChart } from 'react-icons/io5';
import { BsColumns, BsCart3 } from 'react-icons/bs';
import { selectGlobalState } from '../../globalSlice';




const close = keyframes`
  from { 
      transform: translate(0vw); 
    }
  to { 
      transform: translate(-15vw); 

      }
`;

const open = keyframes`
  from { 
      transform: translate(-15vw); 
    }
  100%{ 
      transform: translate(0vw); 
      }
`;

const NewSideBar = ({ isOpen, onToggle }) => {
  const { isDistributor } = useSelector(selectGlobalState);
  const { onboard } = UseOnboardContext()
  const { colorMode } = useColorMode();
  const dispatch = useDispatch();
  const storeLocale = useSelector((state) => get(state, 'language.locale', ''));
  const handleLanguageChange = (event) => {
    dispatch(changeLocale(event.target.value));
  };
  const intl = useIntl();
  const animateOpen = isOpen
    ? `${open} 0.3s linear 0s forwards`
    : `${close} 0s linear 0s forwards`;

  const navObjs = [
    {
      to: '/dashboard',
      icon: 'fas fa-warehouse',
      message: messages.home,
    },
    {
      to: '/distributor',
      icon: <BsColumns className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
      message: messages.distributor,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employees', icon: 'fas fa-users', message: messages.employee },
    {
      to: '/parts',
      icon: <RiCaravanLine className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
      message: messages.parts,
    },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
    {
      to: '/sales',
      icon: <IoStatsChart className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
      message: messages.sales,
    },
  ];

  const employeeNav = [
    {
      to: '/dashboard',
      icon: 'fas fa-warehouse',
      message: messages.home,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employees', icon: 'fas fa-users', message: messages.employee },
    {
      to: '/parts',
      icon: <RiCaravanLine className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
      message: messages.parts,
    },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
    {
      to: '/sales',
      icon: <IoStatsChart className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
      message: messages.sales,
    },
  ];

  const orderNavs = {
    mainNav: 'Orders',
    to: '/orders',
    icon: <FaBuffer className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} />,
    subNavs: [
      { name: messages.placeOrder, to: '/place-order', icon: <BsCart3 /> },
    ],
  }

  const accountNavs = {
    mainNav: 'Account',
    to: '/accounts',
    icon: <MdAccountBalanceWallet />,
    subNavs: [
      { name: messages.outstanding, to: '/outstanding', icon: <FiTrendingDown /> },
      { name: messages.collections, to: '/collections',  icon: <AiOutlineUngroup/>},
    ],
  };

  const inventoryNavs = {
    mainNav: 'Inventory',
    to: '/inventory',
    icon: <MdListAlt />,
    subNavs: [
      { name: messages.stockTransfer, to: '/stock-transfer', icon: <BiTransfer/>},
    ],
  };

  const navObjs2 = [
    {
      to: '/distributor',
      icon: <BsColumns className='mr-2 text-sm' />,
      message: messages.distributor,
    },
    { to: '/branches', icon: 'fas fa-code-branch', message: messages.branch },
    { to: '/employees', icon: 'fas fa-users', message: messages.employee },
    {
      to: '/workshops',
      icon: 'fas fa-store',
      message: messages.workshops,
    },
  ];

  return (
    <>
      <Box
        animation={animateOpen}
        backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
        width={{ base: 'none', lg: '15vw' }}
        minHeight='100%'
        left='0px'
        position='absolute'
        zIndex={3}
        boxShadow='xs'
      >
        <Box
          as='nav'
          backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
        >
          <Box backgroundColor={colorMode === 'dark' ? 'black' : 'white'}>
            <Box mt={6} display='grid' placeItems='center'>
              <NavLink
                className='w-20 h-20 rounded-full flex justify-center'
                to={(isDistributor && !localStorage.onboard) || !isDistributor? '/dashboard' : '/distributor'}
              >
                <img src={logo} alt='Logo' className='max-w-full rounded' />
              </NavLink>
            </Box>
            <Box className='text-center text-lg font-bold mt-4'>
              <FormattedMessage {...messages.garaazAdmin} />
            </Box>
            <div className='md:flex-col md:min-w-full flex flex-col list-none'>
              <hr className='my-4 md:min-w-full' />
              <h6 className='md:min-w-full text-blueGray-500 text-xs pl-4  uppercase font-bold block pt-1 pb-4 no-underline'>
                <FormattedMessage {...messages.distributor} />
              </h6>
              {onboard && isDistributor
                ? navObjs2.map((item) => {
                    return (
                      <SingleNav
                        key={item.to}
                        to={item.to}
                        icon={item.icon}
                        message={item.message}
                      />
                    );
                  })
                : (isDistributor?  navObjs : employeeNav).map((item) => {
                    return (
                      <SingleNav
                        key={item.to}
                        to={item.to}
                        icon={item.icon}
                        message={item.message}
                      />
                    );
                  })}
                {((isDistributor && !localStorage.onboard) || !isDistributor) && <GenericGroupNav {...orderNavs} />}
              {((isDistributor && !localStorage.onboard) || !isDistributor)  && (
                <GenericGroupNav
                 {...inventoryNavs}
                />
              )}
              {((isDistributor && !localStorage.onboard) || !isDistributor)  && <GenericGroupNav {...accountNavs} />}
              
            </div>

            <Box
              style={{
                display: 'flex',
                flexDirection: 'column',
                marginTop: 'auto',
                alignItems: 'flex-start',
              }}
              ml={4}
            >
              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <i className='fas fa-language'></i>
                <Select value={storeLocale} onChange={handleLanguageChange}>
                  <option value='en'>Eng</option>
                  <option value='hi'>
                    {' '}
                    {intl.formatMessage(messages.hindi)}
                  </option>
                </Select>
              </div>

              <div
                style={{
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                }}
              >
                <i className='fas fa-sign-out-alt mr-3'></i>
                <NavLink
                  onClick={() => {
                    dispatch(attemptLogout());
                  }}
                  exact
                  to='/login'
                  className={classnames(
                    'text-xs uppercase py-3 font-bold block',
                  )}
                  activeClassName='text-red-500 hover:text-red-700'
                  style={{ display: 'flex', marginLeft: '5px' }}
                >
                  <FormattedMessage {...messages.logout} />
                </NavLink>
              </div>
            </Box>
          </Box>
        </Box>
      </Box>
      <Box
        boxShadow='xs'
        width='5vw'
        backgroundColor={colorMode === 'dark' ? 'black' : 'white'}
        minHeight='100%'
        position='absolute'
        left='0px'
        zIndex={1}
        display={isOpen ? 'none' : 'block'}
      >
        <Box display='grid' placeItems='center' mb={3} mt='12vh'>
          <NavLink to='/dashboard'>
            <Image
              boxSize='5vw'
              objectFit='cover'
              src={logo}
              alt='Garaaz logo'
            />
          </NavLink>
        </Box>

        <Divider mb={6} />
        {[...navObjs, orderNavs, ...orderNavs.subNavs,  inventoryNavs, ...inventoryNavs.subNavs, accountNavs, ...accountNavs.subNavs].map((item) => {
          return <NavIcon key={item.to} to={item.to} icon={item.icon} />;
        })}
        <IconButton my={3} mr={2}>
          <Select value={storeLocale} onChange={handleLanguageChange}>
            <option value='en'>Eng</option>
            <option value='hi'> {intl.formatMessage(messages.hindi)}</option>
          </Select>
        </IconButton>
        <NavLink
          onClick={() => {
            dispatch(attemptLogout());
          }}
          exact
          to='/login'
          className={classnames('text-xs uppercase py-3 font-bold block')}
          activeClassName='text-red-500 hover:text-red-700'
          style={{ display: 'grid', placeItems: 'center' }}
        >
          <IconButton>
            <i className='fas fa-sign-out-alt'></i>
          </IconButton>
        </NavLink>
      </Box>
    </>
  );
};

export const GenericGroupNav = (props) => {
  const { isOpen, onToggle } = useDisclosure();
  const { colorMode } = useColorMode();

  const subNavList = [...props.subNavs].map((nav) => {
    return (
      <SingleNav
        key={nav.to}
        to={nav.to}
        icon={
          <Box className={`${isOpen ? 'mr-2' : 'mr-0'} text-sm`} >
            {nav.icon}
          </Box>
        }
        message={nav.name}
      />
    );
  });

  return (
    <React.Fragment>
      <NavLink
        exact
        to={props.to}
        onClick={onToggle}
        className={classnames(
          'text-xs pl-4 uppercase py-3 font-bold block flex',
        )}
        activeClassName='text-red-500 hover:text-red-700'
      >
        <Box mr={2} fontSize={{ base: 16, lg: 'lg' }}>
          {props.icon}
        </Box>
        {props.mainNav}
      </NavLink>
      <Collapse in={isOpen} animateOpacity>
        <Box backgroundColor={colorMode === 'dark' ? 'black' : 'gray.100'}>
          {subNavList}
        </Box>
      </Collapse>
    </React.Fragment>
  );
};


const SingleNav = ({ to, icon, message }) => {
  return (
    <NavLink
      exact
      to={to}
      className={classnames('text-xs pl-4 uppercase py-3 font-bold block flex')}
      activeClassName='text-red-500 hover:text-red-700'
    >
      {typeof icon === 'string' ? (
        <i className={classnames(icon, 'mr-2 text-sm')} />
      ) : (
        icon
      )}

      <FormattedMessage {...message} />
    </NavLink>
  );
};

const NavIcon = ({ to, icon }) => {
  return (
    <NavLink
      exact
      to={to}
      className={classnames('text-xs  uppercase py-3 font-bold block')}
      activeClassName='text-red-500 hover:text-red-700'
      style={{ display: 'grid', placeItems: 'center' }}
    >
      {typeof icon === 'string' ? (
        <IconButton>
          <i className={classnames(icon, 'text-sm')} />
        </IconButton>
      ) : (
        <IconButton>{icon}</IconButton>
      )}
    </NavLink>
  );
};

export default NewSideBar;
