import messages from "./messages";
import { Button } from "@chakra-ui/button";
import { Input, InputGroup, InputLeftElement } from "@chakra-ui/input";
import { FormattedMessage, useIntl } from "react-intl";
import { LockIcon } from '@chakra-ui/icons'
import PropTypes from 'prop-types';
import brandLogo from "../../images/logo.png"
import OtpInput from 'react-otp-input';
import React, { useState} from "react";
import { Timer } from "./Timer";
import { render } from "@testing-library/react";
import { indexOf } from "lodash";
import { dataAttr } from "@chakra-ui/utils";

const OTP = ({ color, onChange, onResend, OnSubmit, submit, mobileNumber, errors }) => {
  const intl = useIntl();
  const [otp, setOtp] = useState('')
  const handelChange = (otp) => {
    setOtp(otp);
  }
  
  const [timeOut, setTimeOut] = useState(0)
  const [time, setTime] = useState({ initialMinute: 0, initialSeconds: 15 })
 
  return (
    <div style={{ height: "100%", display: "flex", alignItems: "center", flexDirection: "column", justifyContent: "center" }}>
      <h2 className="text-6xp mb-3 font-bold" style={{ alignItems: "center" }}>
        <img width='100' height='100' src={brandLogo} alt="logo" style={{ margin: "19px" }} />
        <FormattedMessage {...messages.pageTitle} />
      </h2>
      <form
        className="flex items-center justify-center flex-col px-12 h-auto text-center"
        onSubmit={(e) => {
          e.preventDefault();
          submit(OnSubmit(otp))
        }}
      >
        <p className="text-blueGray-500">
          {`${intl.formatMessage(messages.otpSentToMobile)} ${mobileNumber}`}
        </p>
        <span className="text-blueGray-500 mt-3 mb-3">
          {intl.formatMessage(messages.otpMessage)}
        </span>
        <div>
          <OtpInput
            shouldAutoFocus={true}
            isInputNum={true}
            type="number"
            value={otp}
            inputStyle={{ borderRadius: 5, width: 60, margin: 5, maxLength:1, minLength: 1 }}
            onChange={handelChange}
            autoFocus={true}
            numInputs={4}
            separator={<span> </span>}
          />
          {errors && errors.otp && (
            <span className="text-youtube-regular text-sm">
              {errors.otp.type === 'required' ? (
                <FormattedMessage {...messages.otpRequired} />
              ) : (
                <FormattedMessage {...messages.otpValid} />
              )}
            </span>
          )}
        </div>
        <span
          className="text-facebook-regular cursor-pointer"
          role="button"
          style={{ outline: "none" }}
          tabIndex="0"
          onClick={() => onChange(mobileNumber)}>
          <FormattedMessage {...messages.changeMobileNumber} />
        </span>
        {!(time.initialSeconds > 0) &&  <Button
          // className="text-facebook-regular cursor-pointer"
          variant="resendOtp"
          role="button"
          tabIndex="0"
          style={{ backgroundColor: "transparent", outline: "none", border: "transparent" }}
          outline="none"
          onClick={timeOut ? () => {
            onResend()
            setTimeOut(0)
            setTime({ initialMinute: 0, initialSeconds: 15 })
          } : null}
        >
          <FormattedMessage {...messages.resendOtp} />
        </Button>}
       <Timer setTimeOut={setTimeOut} time={time} setTime={setTime} />
        <div className="mt-3">
          <Button type="submit" variant="signIn">
            <FormattedMessage {...messages.signIn} />
          </Button>
        </div>
      </form>
    </div>
  )
}

OTP.prototype = {
  color: PropTypes.string,
  onNumberChange: PropTypes.func,
  optSubmit: PropTypes.func,
  onResend: PropTypes.func,
  mobileNumber: PropTypes.string,
  errors: PropTypes.object,
  registerOtp: PropTypes.func
}

export default OTP;
