import React, { useState, useMemo, useEffect } from 'react';
import { useIntl } from 'react-intl';
import Layout from '../Layout';
import { Button, Box, NumberInput, NumberInputField,
  Flex
 } from '@chakra-ui/react';

import DynamicTable from '../../components/DynamicTable';
import { OrderSummary } from './OrderSummary';
import {
  useRouteMatch,
  Route,
  useHistory,
  Switch,
  useParams,
} from 'react-router-dom';
import { OrderDetailInfo } from '../../components/OrderComponents/OrderDetailInfo';
import { OrderOptions } from '../../components/OrderComponents/OrderOptions';
import { PartsUnavailable } from './PartsUnavailable';
import messages from './messages';
import {
  getOrderDetails,
  setOrderDetails,
  getQuantityAvailable,
  updateOrders,
} from './actions';
import { useSelector, useDispatch } from 'react-redux';
import { BackButton } from '../../components/BackButton';
import getPartIds from '../../utils/getPartIds';
import withToast from '../../HOCs/withToast';
import totalItemFromArray from '../../utils/getTotalFromArray';
import getMoneyFormat from '../../utils/getMoneyFormat';

const OrderDetails = ({ notification }) => {
  const intl = useIntl();
  const auth = useSelector((state) => state.auth);
  const { orderDetails } = useSelector((state) => state.orderDetails);
  const dispatcher = useDispatch();
  const { id } = useParams();
  const history = useHistory();
  const { url, path } = useRouteMatch();
  const [skipPageReset, setSkipPageReset] = React.useState(false);
  const [totals, setTotals] = useState({
    totalOrderedQuantity: 0,
    totalQuantityToGive: 0,
    totalAmount: 0,
  });
  const [isLoading, setLoading] = useState(false);
  const [deliveryValue, setDeliveryValue] = useState(
    intl.formatMessage(messages.selfPickup),
  );
  const [paymentValue, setPaymentValue] = useState(
    intl.formatMessage(messages.cash),
  );

  console.log({ orderDetails })

  const updateMyData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    setSkipPageReset(true);
    let row = [...orderDetails.items].find((data, indx) => indx === rowIndex);
    if (parseInt(value) > parseInt(row.quantity)) {
      notification({
        title: intl.formatMessage(messages.outOfRange),
        description: intl.formatMessage(messages.outOfRangeDescription),
        status: 'error',
      });
      return;
    }
    let totalAmount = parseInt(value) * parseInt(row?.price ?? 0);
    totalAmount = isNaN(totalAmount) ? 0 : totalAmount;
    row = {
      ...row,
      totalAmount,
      [columnId]: value,
    };
    const prevTableData = [...orderDetails.items];
    prevTableData.splice(rowIndex, 1, row);
    let newOrderDetails = { ...orderDetails };
    newOrderDetails.items = prevTableData;
    dispatcher(setOrderDetails(newOrderDetails));
  };

  const getStockQuantity = async (parts, branchId, orderDetails) => {
    try {
      const response = await getQuantityAvailable({
        auth,
        params: { parts, branchId },
      });

      const data = [...orderDetails.items].map((item) => ({
        ...item,
        availableQuantity: response.data[item.partId].quantity ?? 0,
        price: response.data[item.partId].price ?? item.price,
      }));

      dispatcher(setOrderDetails({ ...orderDetails, items: data }));
    } catch (error) {
      notification({
        description: intl.formatMessage(messages.errorOccurred),
        status: 'error',
      });
    }
  };

  const fetchOrderDetails = async () => {
    try {
      const data = await getOrderDetails(auth, id);
      if(data.items.length > 0){
        getStockQuantity(getPartIds([...data.items]), data.branchId, data);
      }else {
        dispatcher(setOrderDetails(data));
      }
    } catch (error) {
      notification({
        status: 'error',
        description: error.response
          ? error.response.data.message
          : intl.formatMessage(messages.errorOccurred),
      });
    }
  };

  useEffect(() => {
    setSkipPageReset(false);

    const totalOrderedQuantity = totalItemFromArray(
      orderDetails.items,
      'quantity',
    );
    const totalQuantityToGive = totalItemFromArray(
      orderDetails.items,
      'quantityToGive',
    );
    const totalAmount = totalItemFromArray(orderDetails.items, 'totalAmount');

    setTotals({ totalOrderedQuantity, totalQuantityToGive, totalAmount });
  }, [orderDetails]);

  useEffect(() => {
    fetchOrderDetails();
  }, []);

  const columns = useMemo(
    () => [
      {
        Header: intl.formatMessage(messages.SNo),
        accessor: 'SNo',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: intl.formatMessage(messages.partNumber),
        accessor: (row) => row.part.partNumber,
      },
      {
        Header: intl.formatMessage(messages.partDescription),
        accessor: (row) => row.part.description,
      },
      {
        Header: intl.formatMessage(messages.quantityOrdered),
        accessor: 'quantity',
      },
      {
        Header: intl.formatMessage(messages.availableQuantity),
        accessor: 'availableQuantity',
      },
      {
        Header: intl.formatMessage(messages.quantityToGive),
        accessor: 'quantityToGive',
        Cell: EditableCell,
      },
      {
        Header: intl.formatMessage(messages.pricePerPiece),
        accessor: 'price',
        Cell: ({value}) => getMoneyFormat(value)
      },
      {
        Header: intl.formatMessage(messages.totalAmount),
        accessor: 'totalAmount',
        Cell: ({value}) => getMoneyFormat(value)
      },
    ],
    [],
  );
 

  const deliverOptions = {
    title: intl.formatMessage(messages.deliveryOptions),
    options: [
      intl.formatMessage(messages.selfPickup),
      intl.formatMessage(messages.arrangeDelivery),
    ],
  };

  const paymentOptions = {
    title: intl.formatMessage(messages.paymentOptions),
    options: [
      intl.formatMessage(messages.cash),
      intl.formatMessage(messages.onlinePayment),
      intl.formatMessage(messages.collectLater),
    ],
  };

  const handleSaveAndContinue = async () => {
    try {
      //setLoading(true);
      const newItems = [...orderDetails.items]
      .map((item) => {
        return {
          partId: item.partId,
          quantity: parseInt(item.quantityToGive ?? 0),
          price: item.price,
        };
      })

      
      const updatedParts = {
        paymentOption: paymentValue,
        deliveryOption: deliveryValue,
        items: newItems,
        status: orderDetails.status,
        comment: '',
      };

  

      const unavailablePartsList = [...orderDetails.items]
        .map((item) => {
          return {
            ...item,
            pendingQuantity: item.quantity - (item.quantityToGive ?? 0),
          };
        })
        .filter((item) => item.pendingQuantity !== 0);

      localStorage.partsUnavailable = JSON.stringify(unavailablePartsList);


      if(newItems.length > 0) {
        await updateOrders({
          auth,
          orderId: id,
          data: updatedParts,
        });
      }


      localStorage.orderSummary = JSON.stringify(totals);

      const partList = [...orderDetails.items].filter(item => item.quantityToGive);
      localStorage.partList = JSON.stringify(partList);
      setLoading(false);

      notification({
        title: intl.formatMessage(messages.orderUpdated),
        description: intl.formatMessage(messages.orderUpdatedDescription),
        status: 'success',
      });
      history.push(`${url}/order-summary`);
    } catch (error) {
      notification({
        description: intl.formatMessage(messages.errorOccurred),
        status: 'error',
      });
      setLoading(false);
    }
  };

  return (
    <Switch>
      <Route exact path={path}>
          <BackButton />
          <OrderDetailInfo orderDetails={orderDetails} />
          <OrderOptions
            onDeliveryOptionChange={setDeliveryValue}
            onPaymentOptionChange={setPaymentValue}
            deliveryValue={deliveryValue}
            paymentValue={paymentValue}
            deliverOptions={deliverOptions}
            paymentOptions={paymentOptions}
          />
          <DynamicTable
            columns={columns}
            data={orderDetails.items}
            fetchData={(data) => 'fetch order details here'}
            hidePagination={true}
            updateMyData={updateMyData}
            skipPageReset={skipPageReset}
            totals={totals}
          />
          <Flex justifyContent='flex-end'>
          <Button
            onClick={handleSaveAndContinue}
            variant='normal'
            my={4}
            marginLeft='auto'
            isLoading={isLoading}
          >
            {intl.formatMessage(messages.saveContinue)}
          </Button>
          </Flex>
      </Route>
      <Route path={`${path}/order-summary`}>
        <OrderSummary
          goToUnavilable={() => history.push(`${url}/parts-unavailable`)}
        />
      </Route>
      <Route path={`${path}/parts-unavailable`}>
        <PartsUnavailable />
      </Route>
    </Switch>
  );
};

const EditableCell = withToast(
  ({
    value: initialValue,
    row: { index, original },
    column: { id },
    updateMyData,
    notification, // This is a custom function that we supplied to our table instance
  }) => {
    // We need to keep and update the state of the cell normally
    const [value, setValue] = React.useState(initialValue);
    const intl = useIntl();

    const onChange = (value) => {
      if (parseInt(value) > parseInt(original.quantity)) {
        notification({
          title: intl.formatMessage(messages.outOfRange),
          description: intl.formatMessage(messages.outOfRangeDescription),
          status: 'error',
        });
        setValue('');
        updateMyData(index, id, '');
        return;
      } else {
        setValue(value);
        updateMyData(index, id, value);
      }
    };

    // If the initialValue is changed external, sync it up with our state
    React.useEffect(() => {
      if(initialValue !== undefined){
        setValue(initialValue)
      }else{
        const qty = original.availableQuantity < original.quantity ? original.availableQuantity : original.quantity;
        setValue(qty);
        updateMyData(index, id, qty);
      }
     
    }, [initialValue]);

    return (
      <NumberInput
        value={value}
        onChange={onChange}
        width='10vw'
        margin='0 auto'
      >
        <NumberInputField />
      </NumberInput>
    );
  },
);

export default withToast(OrderDetails);
