import React, { useEffect, useState } from 'react';
import { useIntl } from 'react-intl';
import { useSelector, useDispatch } from 'react-redux';
import { Stepper, Step } from 'react-form-stepper';
import get from 'lodash/get';
import Layout from '../Layout';
import BusinessProfileForm from '../../components/BusinessProfileForm';
import IndividualProfileForm from '../../components/IndividualProfileForm';
import LocationDetailsForm from '../../components/LocationDetailsForm';
import BankingDetailsForm from '../../components/BankingDetailsForm';
import messages from './messages';
import { getDistributorInfo, setDistributorInfo } from './actions';
import { isEmpty } from 'lodash';
import toast from 'react-hot-toast';
import Label from '../../utils/stepperLabel';

const DistributerInfo = () => {
  const { distributor } = useSelector((state) => state.distributorInfo);
  const dispatch = useDispatch();
  const [activeStep, setActiveStep] = useState(1);
  const intl = useIntl();
  const auth = useSelector((state) => state.auth);
  const [instanceDistributorInfo, setInstanceDistributorInfo] = useState({});

  //this incomplete means user skip and jumps to next steps without completing the previous step
  const [incomplete, setIncomplete] = useState({
    business: false,
    individual: false,
    location: false,
    banking: false,
  });
  const getDirtibutorData = async () => {
    const data = await getDistributorInfo(auth);

    const state = {
      business: false,
      individual: false,
      location: false,
      banking: false,
    };
    if (!isEmpty(data.business)) {
      state.business = true;
    }
    if (!isEmpty(data.individual?.document)) {
      state.individual = true;
    }
    if (!isEmpty(data.location)) {
      state.location = true;
    }
    if (!isEmpty(data.banking)) {
      state.banking = true;
    }
    dispatch(setDistributorInfo(state));
    return data;
  };

  useEffect(() => {
    (async () => {
      const data = await getDirtibutorData();
      setInstanceDistributorInfo(data);
    })();
  }, []);

  const { banking, location, business, individual } = distributor;

  useEffect(() => {
    if (!business && activeStep > 1) {
      setIncomplete({ ...incomplete, business: true });
    }
    if (!individual && activeStep > 2) {
      setIncomplete({ ...incomplete, individual: true });
    }
    if (!location && activeStep > 3) {
      setIncomplete({ ...incomplete, location: true });
    }
    if (!banking && activeStep != 4 && incomplete.location) {
      setIncomplete({ ...incomplete, banking: true });
    }
  }, [activeStep]);
  return (
    <>
      <div className='w-full mb-1'>
        <Stepper
          activeStep={activeStep}
          styleConfig={{
            completedBgColor: '#008000',
            activeBgColor: '#da0032',
          }}
          nonLinear
          style={{ paddingBottom: '2px' }}
        >
          <Step
            label={
              <Label
                title={intl.formatMessage(messages.businessProfile)}
                isComplete={business}
              />
            }
            onClick={() => setActiveStep(1)}
            completed={business}
          />
          <Step
            label={
              <Label
                title={intl.formatMessage(messages.individualProfile)}
                isComplete={individual}
              />
            }
            onClick={() => setActiveStep(2)}
            completed={individual}
          />
          <Step
            label={
              <Label
                title={intl.formatMessage(messages.locationDetails)}
                isComplete={location}
              />
            }
            onClick={() => setActiveStep(3)}
            completed={location}
          />
          <Step
            label={
              <Label
                title={intl.formatMessage(messages.bankingDetails)}
                isComplete={banking}
              />
            }
            onClick={() => setActiveStep(4)}
            completed={banking}
          />
        </Stepper>
      </div>
      <div className='flex-auto px-6 pb-6 pt-0'>
        {activeStep === 1 && (
          <BusinessProfileForm
            setActiveStep={setActiveStep}
            distributor={distributor}
            distributorData={getDirtibutorData}
          />
        )}
        {activeStep === 2 && (
          <IndividualProfileForm
            setActiveStep={setActiveStep}
            distributor={distributor}
            distributorData={getDirtibutorData}
          />
        )}
        {activeStep === 3 && (
          <LocationDetailsForm
            setActiveStep={setActiveStep}
            distributor={distributor}
            distributorData={getDirtibutorData}
          />
        )}
        {activeStep === 4 && (
          <BankingDetailsForm
            setActiveStep={setActiveStep}
            distributor={distributor}
            distributorData={getDirtibutorData}
            distributorId={instanceDistributorInfo._id}
          />
        )}
      </div>
    </>
    
  );
};

export default DistributerInfo;
