import React, { forwardRef, useMemo, useEffect, useState } from 'react';
import { Box, Divider, Image } from '@chakra-ui/react';
import DynamicTable from '../../components/DynamicTable';
import { useIntl } from 'react-intl';
import messages from './messages';
import { getInvoiceData } from './actions';
import { useSelector } from 'react-redux';
import moment from 'moment';
import getMoneyFormat from '../../utils/getMoneyFormat';



const OrderInvoice = forwardRef((props, ref) => {
  const intl = useIntl();
  const auth = useSelector((state) => state.auth);
  const { orderDetails } = useSelector((state) => state.orderDetails);
  const [invoiceDetails, setInvoiceDetails] = useState({});

  useEffect(() => {
    (async () => {
      try {
        const response = await getInvoiceData({
          auth,
          orderId: orderDetails._id,
        });

        setInvoiceDetails(response.data);
      } catch (error) {

      }
    })();
  }, []);
  const columns = useMemo(
    () => [
      {
        Header: intl.formatMessage(messages.SNo),
        accessor: 'SNo',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: intl.formatMessage(messages.partNumber),
        accessor: 'partNumber',
      },
      {
        Header: intl.formatMessage(messages.partName),
        accessor: 'partName',
      },
      {
        Header: intl.formatMessage(messages.partDescription),
        accessor: 'description',
      },
      {
        Header: intl.formatMessage(messages.hsnCode),
        accessor: 'hsnCode',
      },
      {
        Header: intl.formatMessage(messages.taxSlab),
        accessor: 'taxSlab',
      },
      {
        Header: intl.formatMessage(messages.quantityOrdered),
        accessor: 'quantity',
      },
      {
        Header: intl.formatMessage(messages.pricePerPiece),
        accessor: 'pricePerPiece',
        Cell: ({value}) => getMoneyFormat(value)
      },
      {
        Header: intl.formatMessage(messages.totalAmount),
        accessor: 'totalAmount',
        Cell: ({ row: { values } }) => {
          return getMoneyFormat(values.pricePerPiece * values.quantity);
        },
      },
    ],
    [],
  );

  return (
    <Box ref={ref}>
      <Box p={4}>
        <Image
          boxSize={{ base: '100px', lg: '5vw' }}
          objectFit='contain'
          src='https://garaaz-store.s3.ap-south-1.amazonaws.com/index.png'
          alt='Garaaz logo'
        />
        <Box pt={4} textStyle='medium'>
          {invoiceDetails?.orderNo}
        </Box>
        <Box textStyle='normal'>
          {' '}
          {moment(invoiceDetails?.createdAt).format('hh:mm:ss a, Do,MMM YYYY')}
        </Box>
      </Box>
      <Divider size='md' />
      <Box
        p={4}
        width='100%'
        display='flex'
        flexWrap='wrap'
        justifyContent='space-between'
      >
        <Box textAlign='left'>
          <Box textStyle='normalBold'>From: </Box>
          <Box textStyle='normal'>{invoiceDetails?.branch?.branchName}</Box>
          <Box textStyle='normal'>
            {invoiceDetails?.distributor?.business?.businessLegalName}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.branch?.location?.addressLine1}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.branch?.location?.addressLine2}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.branch?.location?.city},{' '}
            {invoiceDetails?.branch?.location?.state}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.branch?.contact?.alternateNumbers[0]}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.distributor?.business?.gstin}
          </Box>
        </Box>
        <Box textAlign='right'>
          <Box textStyle='normalBold'>To: </Box>
          <Box textStyle='normal'>{invoiceDetails?.workshop?.name}</Box>
          <Box textStyle='normal'>
            {invoiceDetails?.workshop?.location?.addressLine1}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.workshop?.location?.addressLine2}
          </Box>
          <Box textStyle='normal'>
            {invoiceDetails?.workshop?.location?.city},{' '}
            {invoiceDetails?.workshop?.location?.state}
          </Box>
        </Box>
      </Box>
      <Box
        p={4}
        width='100%'
        display='flex'
        flexWrap='wrap'
        justifyContent='space-between'
      >
        <Box textAlign='left'>
          <Box display='flex'>
            <Box textStyle='normalBold' mr={2}>
              Payment Mode:
            </Box>
            <Box textStyle='normal'>{invoiceDetails?.paymentOption}</Box>
          </Box>
          <Box display='flex'>
            <Box textStyle='normalBold' mr={2}>
              Delivery Mode:
            </Box>
            <Box textStyle='normal'>{invoiceDetails?.deliveryOption}</Box>
          </Box>
        </Box>
        <Box textAlign='right'>
          <Box display='flex' justifyContent='flex-end'>
            <Box textStyle='normalBold' mr={2}>
              Contact Person:
            </Box>
            <Box textStyle='normal'>NA</Box>
          </Box>
          <Box display='flex' justifyContent='flex-end'>
            <Box textStyle='normalBold' mr={2}>
              Contact Number:
            </Box>
            <Box textStyle='normal'>{invoiceDetails?.branch?.contact?.alternateNumbers[0]}</Box>
          </Box>
        </Box>
      </Box>
      <Box py={4} px={2}>
        <Box textStyle='medium'>Order Summary</Box>
        <DynamicTable
          columns={columns}
          data={invoiceDetails?.items ?? []}
          fetchData={(data) => 'fetch order details here'}
          hidePagination={true}
        />
        <Box ml='auto' mr={6} mt={4} width='15vw'>
          <Box display='flex' >
            <Box textStyle='medium' mr={4}>
              CGST:
            </Box>
            <Box textStyle='normal'>{[...invoiceDetails.items ?? []].reduce((prevVal, curVal) => {
              return prevVal + curVal.CGST
            }, 0)}</Box>
          </Box>
          <Box display='flex' >
            <Box textStyle='medium' mr={4}>
              SGST:
            </Box>
            <Box textStyle='normal'>{[...invoiceDetails.items ?? []].reduce((prevVal, curVal) => {
              return prevVal + curVal.SGST
            }, 0)}</Box>
          </Box>
          <Box display='flex' >
            <Box textStyle='medium' mr={4}>
              IGST:
            </Box>
            <Box textStyle='normal'>{[...invoiceDetails.items ?? []].reduce((prevVal, curVal) => {
              return prevVal + curVal.IGST
            }, 0)}</Box>
          </Box>
          <Box display='flex'>
            <Box textStyle='medium' mr={4}>
              Total:
            </Box>
            <Box textStyle='normal'>{getMoneyFormat([...invoiceDetails.items ?? []].reduce((prevVal, curVal) => {
              return prevVal + (curVal.quantity * curVal.pricePerPiece)
            }, 0))}</Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
});

export default OrderInvoice;
