import React, { lazy, Suspense, useState, useEffect } from 'react';
import { useIntl } from 'react-intl';
import { Tabs, TabList, TabPanels, Tab, TabPanel ,Center,Button, Box} from "@chakra-ui/react"
import Layout from '../Layout';
import messages from './messages';
import Loader from './Loader';
import Invites from '../../components/Invites';
import '../../assets/customStyle.css'
import './style.css'
import '../../utils/theme/Addition-Template.css';
import { useHistory } from 'react-router';
import { UseOnboardContext } from '../../App'
import { selectGlobalState } from '../../globalSlice';
import { set } from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import downloadExcel from '../../utils/downloadExcel';
import { setEmployeeList, getEmployees } from '../../components/EmployeeList/actions';
import withToast from '../../HOCs/withToast';

const EmployeeList = lazy(() => import('../../components/EmployeeList'));

const EmployeeDashboard = ({notification}) => {
	const { isDistributor } = useSelector(selectGlobalState);
	const intl = useIntl();
	const history = useHistory();
	const { onboard } = UseOnboardContext()
	const [loading, setLoading] = useState(false);
	const [serverError, setServerError] = useState(false);
	const auth = useSelector((state) => state.auth);
	const dispatch = useDispatch();
	const employees = useSelector((state) => state.employeeList.employees);

	const handleExport = () => {
		const refinedTableData = [...employees].map((data) => {
		  return {
			'Employee Code': data.employeeCode,
			'Employee Name': `${data.userProfile[0]?.firstName} ${data.userProfile[0]?.lastName}`,
			'Branch Name': data.branch[0]?.branchName,
			'Mobile Number': data.userProfile[0]?.mobileNumber,
		  };
		});
		downloadExcel(refinedTableData, 'Employees.xlsx');
	  };


	  useEffect(() => {
		getEmployeeList();
	  }, []);


	  const getEmployeeList = async () => {
		try {
		  setLoading(true);
		  const { data } = await getEmployees(auth);
		  dispatch(setEmployeeList(data));
		  setLoading(false);
		} catch (error) {
		  setLoading(false);
		  setServerError(true);
		  notification({status: 'error', title: 'Error Occurred', description: error});
		}
	  };

	if (onboard && onboard < 3 && isDistributor) {
		return (
		  <Center minHeight='80vh' flexDirection='column'>
			<Box mb={4} textStyle='medium' display='block'>Please complete branch onboarding</Box>
			<Button onClick={() => history.push('/branches')} variant='normal'> Go to branch</Button>
		  </Center>
		);
	  }
	return (
		<>
			<Box minHeight='80vh'>
				<div className="w-full mb-3">
					<Tabs className="emp-tabs" isManual isLazy defaultIndex={0}>
						<TabList className="tab-list" style={{
							width: "",
							overflowX: "auto",
							//height: "56px",
							whiteSpace: "nowrap",
						}}>
							<Tab className="tab">Employees</Tab>
							{/* {isDistributor && <Tab className="tab">Invite</Tab>} */}
						</TabList>
						<TabPanels>
							<TabPanel>
								<Suspense fallback={<Loader />}>
									<EmployeeList handleExport={handleExport} employees={employees} serverError={serverError} loading={loading} />
								</Suspense>
							</TabPanel>
							<TabPanel>
								<Invites />
							</TabPanel>
						</TabPanels>
					</Tabs>
				</div >
				</Box>
		</>
	);
};

export default withToast(EmployeeDashboard);
