import React, { useMemo, useState, useRef, useEffect } from 'react';
import Layout from '../Layout';
import DynamicTable from '../../components/DynamicTable';
import { useIntl } from 'react-intl';
import {
  Box,
  Menu,
  MenuButton,
  Center,
  MenuList,
  Button,
  MenuItem,
} from '@chakra-ui/react';
import ModifiedTableTop from '../../components/TableTop/ModifiedTableTop';
import enums from '../../utils/enums';
import { FiMoreVertical } from 'react-icons/fi';
import readXlsxFile from 'read-excel-file';
import {
  getWorkshopCollections,
  getAllAccounts,
  selectCollectionsAcct,
} from './slice';
import { useSelector, useDispatch } from 'react-redux';
import moment from 'moment';
import {
  useRouteMatch,
  Switch,
  Route,
  useParams,
  useHistory,
} from 'react-router-dom';
import { BackButton } from '../../components/BackButton';
import { UseTitleContext } from '../../App';
import messages from './messages';
import getMoneyFormat from '../../utils/getMoneyFormat';

const WorkshopCollection = (props) => {
  const [inputValues, setInputValue] = useState({
    end: moment().format('DD-MM-YYYY'),
    start: moment().startOf('month').format('DD-MM-YYYY'),
  });
  const intl = useIntl();
  const [isLoading, setIsLoading] = useState(false);
  const auth = useSelector((state) => state.auth);
  const { workshopCollection, workshopCollectionList } =
    useSelector(selectCollectionsAcct);
  const dispatcher = useDispatch();
  const { id } = useParams();
  const { updateTitle } = UseTitleContext();

  useEffect(() => {
    (async () => {
      setIsLoading(true);
      const response = await dispatcher(
        getWorkshopCollections({ auth, id, params: { ...inputValues } }),
      );
      updateTitle(`${response.workshopCollections?.workshopName} accounts`);
      setIsLoading(false);
    })();
  }, [id]);

  const inputData = useMemo(
    () => [
      {
        title: intl.formatMessage(messages.accountDetailSearch),
        type: 'text',
        placeholder: '',
        name: 'searchAccount',
      },
      {
        title: intl.formatMessage(messages.groupBy),
        type: 'select',
        placeholder: 'Select option',
        name: 'groupBy',
        options: [
          {
            value: 'none',
            label: 'None',
          },
          {
            value: 'branch',
            label: intl.formatMessage(messages.branch),
          },
          {
            value: 'salesRep',
            label: intl.formatMessage(messages.salesRep),
          },
          {
            value: 'workshopType',
            label: intl.formatMessage(messages.workshopType),
          },
        ],
      },
      {
        title: intl.formatMessage(messages.date),
        type: 'date',
        placeholder: 'Enter order No',
        name: ['start', 'end'],
      },
    ],
    [],
  );

  const columns = useMemo(
    () => [
      {
        Header: intl.formatMessage(messages.sNo),
        accessor: 'sno',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: intl.formatMessage(messages.workshopName),
        accessor: 'workshopName',
      },
      {
        Header: intl.formatMessage(messages.workshopType),
        accessor: 'workshopType',
      },
      {
        Header: intl.formatMessage(messages.branch),
        accessor: 'branch',
      },
      {
        Header: intl.formatMessage(messages.salesRep),
        accessor: 'salesRep',
      },
      {
        Header: 'Total Collections',
        accessor: 'collections',
        Cell: ({ value }) => getMoneyFormat(value),
      }
    ],
    [],
  );

  const workshopAccountListColumn = useMemo(
    () => [
      {
        Header: intl.formatMessage(messages.sNo),
        accessor: 'sno',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: intl.formatMessage(messages.date),
        accessor: 'createdAt',
        Cell: ({ value }) => moment(value).format('Do MMM, YYYY'),
      },
      {
        Header: intl.formatMessage(messages.particulars),
        accessor: 'particulars',
      },
      {
        Header: intl.formatMessage(messages.vchType),
        accessor: 'vchType',
      },
      {
        Header: intl.formatMessage(messages.vchNumber),
        accessor: 'vchNo',
      },
      {
        Header: intl.formatMessage(messages.credit),
        accessor: 'credit',
        Cell: ({ value }) => getMoneyFormat(value),
      },
    ],
    [],
  );

  const handleTableTopChange = async (e, name) => {
    let value = e.hasOwnProperty('target') ? e.target.value : e;
    if (name === 'end' || name === 'start') {
      value = moment(value).format('DD-MM-YYYY');
    }

    setInputValue((prevState) => ({
      ...prevState,
      [name]: typeof value === 'object' ? value.value : value,
    }));

    await dispatcher(
      getWorkshopCollections({
        auth,
        id,
        params: {
          ...inputValues,
          [name]:
            typeof value !== 'object'
              ? value
              : value.value === 'none'
              ? ''
              : value.value,
        },
      }),
    );
  };

  if (isLoading) {
    return <Box minHeight='80vh'>Loading...</Box>;
  }

  return (
    <Box minHeight='80vh'>
      <BackButton />
     {/*  <ModifiedTableTop inputData={inputData} onChange={handleTableTopChange} /> */}
     <Box mt={2}></Box>
      <DynamicTable
        columns={columns}
        data={workshopCollection}
        fetchData={() => console.log('...fetching')}
        skipPageReset={true}
      />
      <Box pt={10}>
        <DynamicTable
          columns={workshopAccountListColumn}
          data={workshopCollectionList}
          fetchData={() => console.log('...fetching')}
          skipPageReset={true}
        />
      </Box>
    </Box>
  );
};

export default WorkshopCollection;
