import toast from 'react-hot-toast';
import { publicAxios as axios } from '../../axios'

export const getWorkShop = async (auth, distributorId, workshopId) => {
  try {
    const { data } = await axios.get(`/distributors/${distributorId}/workshops/${workshopId}`, {
      headers: {
        Authorization: auth.authToken,
      },
    });
    return data
  } catch (error) {
    toast("Something went wrong, while getting workshop details!")
  }
}

export const addWorkShop = async (auth, payload, distributorId) => {
  return await axios.post(`/distributors/${distributorId}/workshops`, { workshops: [payload] },

    {
      headers: {
        Authorization: auth.authToken,
      },
    })
}
export const editWorkshop = async (auth, payload, distributorId, workshopId) => {
  return await axios.patch(`/distributors/${distributorId}/workshops/${workshopId}`,
    payload,
    {
      headers: {
        Authorization: auth.authToken,
      },
    })
}
