import { Button, IconButton } from "@chakra-ui/button";
import { PhoneIcon, SmallCloseIcon } from "@chakra-ui/icons";
import { Input, InputGroup, InputLeftElement, InputRightElement } from "@chakra-ui/input";
import { FormattedMessage } from "react-intl";
import messages from "./messages";
import PropTypes from 'prop-types';
import brandLogo from "../../images/logo.png"

const LoginDetails = ({ register, errors, submit, OnSubmit, setMobile }) => {
  return (
    <div style={{ height: "100%", display: "flex", alignItems: "center", flexDirection: "column", justifyContent: "center" }}>
      <h2 className="text-6xp mb-3 font-bold" style={{ alignItems: "center" }}>
        <img width='100' height='100' src={brandLogo} alt="logo" style={{ margin: "19px" }} />
        <FormattedMessage {...messages.pageTitle} />
      </h2>
      <form
        className="flex items-center justify-center flex-col px-12 h-auto text-center"
        onSubmit={submit(OnSubmit)}
      >
        <span className="text-blueGray-500 mt-6 mb-3">
          <FormattedMessage {...messages.phoneNumberMessage} />
        </span>
        <div style={{ display: "flex" }}>
          <p style={{ width: "30%", backgroundColor: "#fff", paddingTop: "7px", height: "100%", borderRadius: "6px", marginRight: "3%" }}>+91 </p>
          <InputGroup>
            <Input
              type="number"
              style={{ backgroundColor: "#fff" }}
              placeholder="9876543210"
              {...register('phoneNumber', {
                required: true,
                pattern: /^\d{10}$/,
                maxLength: 10,
                minLength: 10,
              })}
              autoFocus
              onChange={() => { }}
              onKeyUp={(event) => {
                setMobile('phoneNumber', event.target.value)
              }}
            />
            <InputRightElement border="none">
              <button type="reset" style={{ outline: "none" }} >{<SmallCloseIcon />}</button>
            </InputRightElement>
          </InputGroup>
        </div>
        {errors && errors.phoneNumber && (
          <span className="text-youtube-regular text-sm">
            {errors.phoneNumber.type === 'required' ? (
              <FormattedMessage {...messages.phoneNumberRequired} />
            ) : (
              <FormattedMessage {...messages.phoneNumberValid} />
            )}
          </span>
        )}
        <div className="mt-3">
          <Button type="submit" variant="getOtp">
            <FormattedMessage {...messages.getOtp} />
          </Button>
        </div>
      </form>
    </div>
  );
}
LoginDetails.prototype = {
  register: PropTypes.func,
  errors: PropTypes.object,
  color: PropTypes.string,
  handleSubmit: PropTypes.func,
  handleOnSubmit: PropTypes.func
}
export default LoginDetails;
