import React from 'react'
import { Heading, Button, Image, Flex, Text } from '@chakra-ui/react'
// import serverError from '../../assets/img/serverError.svg'
const Empty = ({ title, message, onAdd, illustrator }) => {
  return (
    <Flex direction="column" width="100%" justifyContent="center">
      <Image alignSelf="center" m={0} src={illustrator} width={600} height={300}></Image>
      <Heading alignSelf="center" mt={10} md={10}>No Data</Heading>
      <Text alignSelf="center" textAlign="center" fontSize="lg">{message}</Text>
      <Button style={{ marginTop: "10px" }} onClick={onAdd} alignSelf="center" bg="#b3d4fc" width="fit-content" color="black" colorScheme="navy" variant="solid">
        <i className="fas fa-plus" > </i>&nbsp;Add {title}
      </Button>
    </Flex >
  )
}
export default Empty;
