import { Button } from '@chakra-ui/button';
import React, { useEffect, useState } from 'react';
import { useIntl } from 'react-intl';
import { useSelector } from 'react-redux';
import { Link, useParams } from 'react-router-dom';
import Layout from '../../containers/Layout';
import messages from './messages';
import { useHistory } from 'react-router';
import { getPart } from './action';
import toast from 'react-hot-toast';
import { Image, Box, Flex } from '@chakra-ui/react';
//import ReactImageMagnify from 'react-image-magnify';
import ReactImageMagnify from '@blacklab/react-image-magnify';
//styles
const PartInfo = () => {
  const auth = useSelector((state) => state.auth);
  const intl = useIntl();
  const { partId } = useParams();
  const [part, setPart] = useState({ partmaster: {} });
  const history = useHistory();
  const getPartsData = async () => {
    try {
      const data = await getPart(auth, partId);
      setPart(data);
    } catch (error) {
      toast(intl.formatMessage(messages.somethingwentwrong));
    }
  };
  useEffect(() => {
    getPartsData();
  }, []);
  const {
    description = '',
    distributorId,
    hsnCode,
    isDeleted,
    manufacturer = [{}],
    manufacturerId,
    media = [],
    mrp = '',
    notes,
    origin = '',
    packQuantity,
    packSize,
    partName = '',
    partNumber = '',
    partType,
    rootPartNumber,
    saleStatus,
    sellingPrice = '',
    status,
    subCategoryId,
    taxSlab,
    category,
  } = part?.partmaster;

  const [selectedImage, setSelectedImage] = useState('');

  useEffect(() => {
    setSelectedImage(media[0] || manufacturer[0]?.logo);
  }, [part]);

  return (
    <Box minHeight='80vh'>
      <div className='mb-0 p-6 pb-0'>
        <div className='flex px-6 pb-6 pt-0'>
          <Link to='/parts' style={{ color: '#1e293b', fontWeight: 'bold' }}>
            Parts List
          </Link>{' '}
          /<p>{partName}</p>
        </div>
        <div
          className='text-center flex items-center'
          style={{ justifyItems: 'flex-end' }}
        >
          <Button
            variant='bulkAdd'
            size='md'
            onClick={() => history.push(`/part/${part._id}/edit`)}
          >
            <i className='fas fa-pen' style={{ marginRight: '5px' }} />
            {intl.formatMessage(messages.edit)}
          </Button>
        </div>
      </div>

      <div className='flex px-6 pb-6 pt-0' style={{ flexDirection: 'row' }}>
        <Box style={{ width: '40%', height: '60%', margin: '20px' }}>
          <Image
            height='auto'
            width='100%'
            objectFit='cover'
            src={selectedImage}
          />
        </Box>
        <Box ml='3' width='60%'>
          <p style={{ fontSize: '22px', fontWeight: 'bold' }}>{partName}</p>
          <br />
          <Flex flexWrap='wrap'>
            <Flex>
            <p
              style={{
                fontSize: '15px',
                marginRight: '5px',
                fontWeight: 'bold',
              }}
            >
              Category:{' '}
            </p>
            <p style={{ fontSize: '15px' }}>{category} | </p>
            </Flex>
            <Flex>
            <p
              style={{
                fontSize: '15px',
                marginRight: '5px',
                fontWeight: 'bold',
              }}
            >
              &nbsp;Part Number:{' '}
            </p>
            <p style={{ fontSize: '15px' }}>{partNumber} | </p>
            </Flex>
            <Flex>
            <p
              style={{
                fontSize: '15px',
                marginRight: '5px',
                fontWeight: 'bold',
              }}
            >
              &nbsp;Brand:{' '}
            </p>
            <p style={{ fontSize: '15px' }}>{manufacturer[0]?.name} | </p>
            </Flex>
            <Flex>
            <p
              style={{
                fontSize: '15px',
                marginRight: '5px',
                fontWeight: 'bold',
              }}
            >
              &nbsp;Origin:{' '}
            </p>
            <p style={{ fontSize: '15px' }}>{origin} </p>
            </Flex>
          </Flex>
          <br />
          <div style={{ display: 'flex' }}>
            <p style={{ fontWeight: 'bold' }}>INR {part?.sellingPrice} &nbsp;</p>
            <strike>&nbsp;{mrp}</strike>
            <p style={{ color: 'green' }}>
              &nbsp; ({(((mrp - part?.sellingPrice) / mrp) * 100).toFixed(2)}% off)
              &nbsp; Inclusive of all taxes
            </p>
          </div>
          <br />
          <p
            style={{
              fontSize: '15px',
              fontWeight: 'bold',
              marginBottom: '10px',
            }}
          >
            Part Description:{' '}
          </p>
          <p>{part?.description}</p>
        </Box>
      </div>
    </Box>
  );
};

export default PartInfo;
