import axios from "axios"
import { constants } from "./constants"
export const getStates = async () => {
  const { data } = await axios.get(`${constants.placesurl}/countries/IN/states`, {
    headers: {
      // Authorization: `Bearer ${constants.authToken}`
      "X-CSCAPI-KEY": `${constants.authToken}`

    }
  })
  return data
}

export const getCities = async (stateCode) => {
  const { data } = await axios.get(`${constants.placesurl}/countries/IN/states/${stateCode}/cities`, {
    headers: {
      // Authorization: `Bearer ${constants.authToken}`
      "X-CSCAPI-KEY": `${constants.authToken}`
    }
  })
  return data
}
