import produce from 'immer';

import { SET_DISTRIBUTOR_INFO } from './constants';

export const initialState = {
  distributor: {
    banking: false, location: false, business: false, individual: false
  },
};

/* eslint-disable default-case, no-param-reassign */
const distributorInfoReducer = (state = initialState, action) =>
  produce(state, draft => {
    switch (action.type) {
      case SET_DISTRIBUTOR_INFO:
        draft.distributor = action.payload;
        break;
    }
  });

export default distributorInfoReducer;
