import React from 'react';
import { Tr, Td, Button } from '@chakra-ui/react';
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverBody,
  PopoverArrow,
  Center,
  IconButton,
} from '@chakra-ui/react';
import { useHistory } from 'react-router';
import { FiMoreVertical } from 'react-icons/fi';

const Employee = ({ workshop, onOpen, isDistributor }) => {
  const history = useHistory();
  const {
    name = '',
    type = '',
    branchCode = '',
    code='',
    mobileNumber = '',
    creditDays = '',
    discount = '',
    paymentTerms = '',
    branch={},
    ownerFullName=''
  } = workshop;

  return (
    <Tr style={{ borderBottom: '1px solid #cfcfd0' }}>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {code}
      </Td>
      <Td style={{ borderRight: '0.5px solid #cfcfd0', textAlign: 'left' }}>
        {name}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {type}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {branch?.branchCode}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {ownerFullName}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {mobileNumber}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {creditDays}
      </Td>
      <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {discount}%
      </Td>
      {/* <Td style={{ borderRight: '1px solid #cfcfd0', textAlign: 'left' }}>
        {paymentTerms}
      </Td> */}
      {isDistributor && <Td>
        <Popover>
          <PopoverTrigger className='menu-dots'>
            <IconButton>
              <Center>
                <FiMoreVertical />
              </Center>
            </IconButton>
          </PopoverTrigger>
          <PopoverContent style={{ width: 'auto' }}>
            <PopoverArrow />

            <PopoverBody style={{ justifyContent: 'center', display: 'flex' }}>
              <Button
                onClick={() => {
                  history.push(`/workshop/${workshop._id}/edit`);
                }}
                style={{ backgroundColor: 'gray', color: '#ffffff' }}
              >
                Edit
              </Button>
            </PopoverBody>
          </PopoverContent>
        </Popover>
      </Td>}
    </Tr>
  );
};
export default Employee;
