import { HTMLProps, memo } from 'react';
import type { HintProps } from 'src/types';

export type PropTypes = HintProps & HTMLProps<HTMLDivElement>;

export const DefaultHint = memo((props: PropTypes): JSX.Element => {
    const {
        isMouseDetected,
        isTouchDetected,
        hintTextMouse,
        hintTextTouch,
        style,
        ...rest
    } = props;

    return (
        <div
            style={{
                width: '100%',
                display: 'flex',
                justifyContent: 'center',
                position: 'absolute',
                bottom: '25px',
                zIndex: 99,
                ...style,
            }}
            {...rest}
        >
            <div
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    padding: '5px 10px',
                    backgroundColor: '#333',
                    borderRadius: '10px',
                    opacity: '0.90',
                }}
            >
                <img
                    style={{
                        width: '25px',
                        height: '25px',
                    }}
                    src="data:image/svg+xml;utf8;base64,"
                    alt=""
                />
                <span style={{
                    padding: '2px 0 0 5px',
                    fontFamily: 'Arial, sans-serif',
                    fontSize: '14px',
                    color: 'white',
                }}
                >
                    { isTouchDetected ? hintTextTouch : hintTextMouse }
                </span>
            </div>
        </div>
    );
});
