import React, { useEffect, useState } from 'react';
import { Button, Box } from '@chakra-ui/react';
import toast from 'react-hot-toast';
import { useHistory } from 'react-router';
import { useSelector } from 'react-redux';
import { getWorkshops } from './actions';
import messages from './messages';
import serverErrorIllustrator from '../../assets/img/serverError.svg';
import Error from '../Error';
import { useIntl } from 'react-intl';
import './style.css';
import { optionsProp } from './constants';
import WorkshopTable from './Table/WorkshopTable';
import AdditionOption from '../../utils/AdditionOptions';
import { Loading } from '../Loading';
import { selectGlobalState } from '../../globalSlice';
import { Link } from 'react-router-dom';


const WorkshopList = () => {
  const [serverError, setServerError] = useState(false);
  const intl = useIntl();
  const auth = useSelector((state) => state.auth);
  const [openBulk, setOpenBulk] = useState(false);
  const [loading, setLoading] = useState(false);
  const {workshops, distributor, isDistributor} = useSelector(selectGlobalState);



  return (
    <div>
      <div className='mb-0 p-1 pb-0 '>
        {workshops.length > 0  && isDistributor && (
          <Box textAlign='right'>
            <Link to='/workshop/add'>
              <Button variant='normal' size='md'>
                <i className='fas fa-user-plus mr-2'></i>
                {intl.formatMessage(messages.addWorkshop)}
              </Button>
            </Link>
          </Box>
        )}
        {!navigator.onLine ? (
          <Error
            onRetry={() => getWorkshops({auth, distributorId: distributor._id})}
            title={intl.formatMessage(messages.networkErrorTitle)}
            message={intl.formatMessage(messages.networkErrorMessage)}
            illustrator={serverErrorIllustrator}
          />
        ) : serverError ? (
          <Error
            onRetry={() => getWorkshops({auth, distributorId: distributor._id})}
            title={intl.formatMessage(messages.serverErrorTitle)}
            message={intl.formatMessage(messages.serverErrorMessage)}
            illustrator={serverErrorIllustrator}
          />
        ) :loading? <Loading/> : workshops.length > 0 ? (
          <WorkshopTable workshops={workshops} isOpen={openBulk} />
        ) : (
          <AdditionOption {...optionsProp} />
        )}
      </div>
    </div>
  );
};



export default WorkshopList;
