import { defineMessages } from 'react-intl';

export const scope = 'app.containers.LoginPage';

export default defineMessages({
  pageTitle: {
    id: `${scope}.pageTitle`,
    defaultMessage: 'Garaaz Distributer',
  },
  phoneNumberMessage: {
    id: `${scope}.phoneNumberMessage`,
    defaultMessage: 'Enter your phone number to get OTP',
  },
  phoneNumberRequired: {
    id: `${scope}.phoneNumberRequired`,
    defaultMessage: 'Phone number field is required.',
  },
  phoneNumberValid: {
    id: `${scope}.phoneNumberValid`,
    defaultMessage: 'Enter a valid phone number.',
  },
  getOtp: {
    id: `${scope}.getOtp`,
    defaultMessage: 'Get OTP',
  },
  otpMessage: {
    id: `${scope}.otpMessage`,
    defaultMessage: 'Enter the OTP to sign-in',
  },
  otpRequired: {
    id: `${scope}.otpRequired`,
    defaultMessage: 'OTP field is required.',
  },
  otpValid: {
    id: `${scope}.otpValid`,
    defaultMessage: 'Enter a valid OTP.',
  },
  signIn: {
    id: `${scope}.signIn`,
    defaultMessage: 'Sign In',
  },
  resendOtp: {
    id: `${scope}.resendOtp`,
    defaultMessage: 'Resend OTP',
  },
  somethingWentWrong: {
    id: `${scope}.somethingWentWrong`,
    defaultMessage: 'Something went wrong while login.',
  },
  exhaustedOtpLimits: {
    id: `${scope}.exhaustedOtpLimits`,
    defaultMessage: 'You have used maximum OTP requests.',
  },
  enterCorrectOtp: {
    id: `${scope}.enterCorrectOtp`,
    defaultMessage: 'Please enter correct OTP.',
  },
  otpSentSuccess: {
    id: `${scope}.otpSentSuccess`,
    defaultMessage: 'OTP has been sent successfully.',
  },
  changeMobileNumber: {
    id: `${scope}.changeMobileNumber`,
    defaultMessage: 'Change mobile number',
  },
  otpSentToMobile: {
    id: `${scope}.otpSentToMobile`,
    defaultMessage: 'OTP sent successfully on : ',
  },
});
