import { publicAxios as axios } from '../../axios';
import { api } from './constants';
export const getBrands = async () => {
  try {
    const { data } = await axios.get(api.GET_BRANDS, {});
    return data;
  } catch (error) {
    return error
  }
}

export const addBulkWorkshop = async (auth, distId, data,) => {

  const res = await axios.post(
    `${api.ADD_WORKSHOP_BULK}/${distId}/workshops`,
    {
      workshops: data,
    },
    {
      headers: {
        Authorization: auth.authToken,
      },
    },
  );

  return res
}
