import React, { useEffect, useRef, useState } from 'react';
import PropTypes from 'prop-types';
import { useForm } from 'react-hook-form';
import { useIntl } from 'react-intl';
import toast from 'react-hot-toast';
import { useSelector } from 'react-redux';
import messages from './messages';
import { isEmpty, isUndefined, get } from 'lodash';
import FormactionButtons from '../../utils/FormactionButtons';
import {
  getCategories,
  getPart,
  patchPart,
} from '../../containers/PartAddition/action';
import { Box } from '@chakra-ui/react';
import InputScaffold from '../../utils/InputScaffold';

const CategoryDetailsForm = ({
  setActiveStep,
  setCurrentPart,
  categoryDetails = {},
  currentPart,
  isEdit,
}) => {
  const intl = useIntl();
  const auth = useSelector((state) => state.auth);
  const [categories, setCategories] = useState([]);
  const [description, setDescription] = useState('');

  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
    setValue,
  } = useForm({
    mode: 'onSubmit',
  });

  const fetchCategories = async () => {
    setCategories(await getCategories());
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  /*  useEffect(() => {
    try {
      const cat = categories.filter(
        (category) => category.category === 'All Parts',
      );
      setDefaultCategory(cat);
    } catch (error) {
      toast(intl.formatMessage(messages.allCategoriesNotPresent));
    }
    
    if (currentPart._id) {
      setValue('category', get(currentPart.category[0], '_id', ''));
      setCategory({
        value: currentPart.category[0]._id,
        label: currentPart.category[0].category,
      });



    } else if (!isUndefined(currentPart.category)) {
      setValue('category', get(currentPart, 'category', ''));
      setCategory(currentPart.categorySelect);
    }
  }, [categories]); */

  useEffect(() => {
    if (!isEmpty(currentPart)) {
      setValue(
        'rootPartNumber',
        get(
          currentPart,
          'rootPartNumber',
          isEdit ? currentPart?.partmaster.rootPartNumber : currentPart?.rootPartNumber,
        ),
      );
      setValue(
        'partNumber',
        get(
          currentPart,
          'partNumber',
          isEdit ? currentPart?.partmaster.partNumber : currentPart?.partNumber,
        ),
      );
      setValue(
        'partName',
        get(
          currentPart,
          'partName',
          isEdit ? currentPart?.partmaster.partName : currentPart?.partName,
        ),
      );
      setValue('mrp', get(currentPart, 'mrp', ''));
      setValue('description', get(currentPart, 'description', ''));
      setValue(
        'sellingPrice',
        get(
          currentPart,
          'sellingPrice'
        ),
      );
      setValue(
        'hsnCode',
        get(
          currentPart,
          'hsnCode',
          isEdit ? currentPart?.partmaster.hsnCode : currentPart?.hsnCode,
        ),
      );
      setValue(
        'taxSlab',
        get(
          currentPart,
          'taxSlab',
          isEdit ? currentPart?.partmaster.taxSlab : currentPart?.taxSlab,
        ),
      );
      setValue(
        'category',
        get(
          currentPart,
          'category',
          isEdit ? currentPart?.partmaster.category : currentPart?.category,
        ),
      );
    }
  }, [currentPart]);

  const handleFormSubmit = async (data) => {
  
    if (
      isUndefined(currentPart?.partmaster) ||
      isUndefined(currentPart._id)
    ) {
      setCurrentPart({ ...currentPart, ...data, description });
      setActiveStep(3);
    } else {
      try {
        

        const { partmaster, partMasterId, ...editableData } = {
          ...currentPart,
          ...data,
        };

        delete editableData.partNumber;
        delete editableData.partName;
        delete editableData.rootPartNumber;
        delete editableData.saleStatus;
        delete editableData.description;
        delete editableData.hsnCode;
        delete editableData.taxSlab;
        delete editableData.status;
        delete editableData.updatedAt;
        delete editableData.createdAt;

          await patchPart(auth, { ...editableData });
        const response = await getPart(auth, currentPart._id);
        setCurrentPart(response);
        toast(intl.formatMessage(messages.partUpdateSuccess));
        setActiveStep(3);
      } catch (error) {
        toast(intl.formatMessage(messages.somethingWentWrong));
      }
    }
  };

  const onReset = () => {
    reset(
      {},
      {
        keepDirty: false,
        keepErrors: false,
        keepValues: false,
      },
    );
  };

  const handleDescription = (value) => {
    setDescription(value.target.value);
  };
  return (
    <form onSubmit={handleSubmit(handleFormSubmit)}>
      <div>
        <h6 className='mt-6 mb-2 font-bold'>
          {intl.formatMessage(messages.categoryDetails)}
        </h6>
        <hr className='mb-6 border-b-1 border-blueGray-200' />
        <Box>
          <Box display='flex' justifyContent='space-between' flexWrap='wrap'>
            {/* <Box width={{ base: '100%', lg: '48%' }}>
              <label className='block uppercase text-blueGray-500 text-xs font-bold mb-2 ml-1'>
                {intl.formatMessage(messages.category)}
              </label>
              <SearchSelect
                style={{
                  width: 320,
                }}
                value={category}
                onChange={(e) => {
                  setCategory(e);
                  setValue('category', e.value);
                  setValue('categorySelect', e);
                }}
                options={categories.map((category) => {
                  return { value: category._id, label: category.category };
                })}
                placeholder={intl.formatMessage(messages.selectCategory)}
              ></SearchSelect>
              {errors && errors.brand && (
                <span className='text-youtube-regular text-sm'>
                  {intl.formatMessage(messages.categoryRequired)}
                </span>
              )}
            </Box> */}
            <InputScaffold
              width='48%'
              label={messages.category}
              inputName='category'
              inputType='text'
              isRequired={false}
              errors={errors}
              requiredLabel={messages.categoryRequired}
              register={register}
            />
            <InputScaffold
              width='48%'
              label={messages.rootPartNumber}
              inputName='rootPartNumber'
              inputType='text'
              isRequired={false}
              errors={errors}
              requiredLabel={messages.rootPartNumberRequired}
              register={register}
              isReadOnly={isEdit}
            />
          </Box>
          <Box
            flexWrap='wrap'
            display='flex'
            justifyContent='space-between'
            mt={{ base: 0, lg: 6 }}
            mb={6}
          >
            <InputScaffold
              width='48%'
              label={messages.partNumber}
              inputName='partNumber'
              inputType='text'
              isRequired={true}
              errors={errors}
              requiredLabel={messages.partNumberRequired}
              register={register}
              isReadOnly={isEdit}
            />
            <InputScaffold
              width='48%'
              label={messages.partName}
              inputName='partName'
              inputType='text'
              isRequired={true}
              errors={errors}
              requiredLabel={messages.partNameRequired}
              register={register}
              isReadOnly={isEdit}
            />
          </Box>
          <Box
            flexWrap='wrap'
            display='flex'
            justifyContent='space-between'
            mt={{ base: 0, lg: 6 }}
            mb={6}
          >
            <InputScaffold
              width='48%'
              label={messages.mrp}
              inputName='mrp'
              inputType='number'
              isRequired={true}
              errors={errors}
              requiredLabel={messages.mrpRequiredMessage}
              register={register}
            />
            <InputScaffold
              width='48%'
              label={messages.sellingPrice}
              inputName='sellingPrice'
              inputType='number'
              isRequired={false}
              errors={errors}
              register={register}
            />
          </Box>
          <Box
            flexWrap='wrap'
            display='flex'
            justifyContent='space-between'
            mt={{ base: 0, lg: 6 }}
            mb={6}
          >
            <InputScaffold
              width='48%'
              label={messages.hsn}
              inputName='hsnCode'
              inputType='number'
              isRequired={true}
              errors={errors}
              requiredLabel={messages.hsnRequiredMessage}
              register={register}
              isReadOnly={isEdit}
            />
            <InputScaffold
              width='48%'
              label={messages.taxSlab}
              inputName='taxSlab'
              inputType='number'
              isRequired={true}
              errors={errors}
              requiredLabel={messages.taxSlabRequiredMessage}
              register={register}
              isReadOnly={isEdit}
            />
          </Box>
          {/* <Box mb={6}>
            <label  className='block uppercase text-blueGray-500 text-xs font-bold mb-2 ml-1'>
              Description
            </label>
            <Textarea {...register('description')} onChange={handleDescription} name='description' isRequired={false} />
          </Box> */}
        </Box>
        <FormactionButtons
          saveLabel={messages.saveButtonLabel}
          onReset={onReset}
          cancleLabel={messages.cancelButtonLabe}
        />
      </div>
    </form>
  );
};

CategoryDetailsForm.propTypes = {
  setActiveStep: PropTypes.func,
  setCurrentPart: PropTypes.func,
};

export default CategoryDetailsForm;
