import { useToast } from "@chakra-ui/react"

const withToast = (WrappedComponent) => {
    return (props) => {
        const toast = useToast();
        const notification = ({status, title = '', description}) => {
            return toast({
                title: title,
                description: description,
                status: status,
                duration: 9000,
                isClosable: true,
                position: 'top'
              })
        }

        return <WrappedComponent {...props} notification={notification}/>
    }
}

export default withToast;
