import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import toast from 'react-hot-toast';
import Error from '../Error'
import { getParts } from './actions';
import serverErrorIllustrator from '../../assets/img/serverError.svg'
import AdditionOption from '../../utils/AdditionOptions';
import { optionsProp } from './constants'
import Table from './Table';


const PartList = () => {
  const [parts, setParts] = useState([])
  const [serverError, setServerError] = useState(false)
  const auth = useSelector(state => state.auth);

  const fetchParts = async () => {
    try {
      const data = await getParts(auth)
      setParts(data)
    } catch (error) {
      setServerError(true)
      toast("Something went wrong!")
    }
  }
  useEffect(() => {
    fetchParts()
  }, [])

  return (
    <div className="h-full">
      <div className="mb-0 p-1 pb-0 h-full">
        {
          serverError ?
            < Error onRetry={getParts}
              title="Server Error"
              message="Something went wrong, please try again!"
              illustrator={serverErrorIllustrator}
            /> :
            (
                <Table />
            )
        }
      </div>
    </div>
  );
};

export default PartList;
