import React, { useEffect, useState, useMemo } from 'react';
import { useIntl } from 'react-intl';
import { useSelector } from 'react-redux';
import BranchItemCard from '../../components/BranchCard';
import messages from './messages';
import toast from 'react-hot-toast';
import Error from '../../components/Error';
import serverErrorIllustrator from '../../assets/img/serverError.svg';
import { optionsProp } from './constants';
import { Button } from '@chakra-ui/button';
import { Link } from 'react-router-dom';
import AdditionOption from '../../utils/AdditionOptions';
import './style.css';
import {
  Box,
  Image,
  Center,
  MenuButton,
  MenuList,
  Menu,
  MenuItem,
} from '@chakra-ui/react';
import DynamicTable from '../../components/DynamicTable';
import { Loading } from '../../components/Loading';
import { FiMoreVertical } from 'react-icons/fi';
import { useHistory } from 'react-router';
import { ROUTES } from '../../routes';
import ModifiedTableTop, {
  SingleInput,
} from '../../components/TableTop/ModifiedTableTop';
import downloadExcel from '../../utils/downloadExcel';
import { selectGlobalState } from '../../globalSlice';
import garaazLogo from '../../images/placeholderImage.jpg';

const BranchList = () => {
  // const [branches, setBranches] = useState([]);
  const { branches, isDistributor } = useSelector(selectGlobalState);
  const [tableData, setTableData] = useState([]);
  const [serverError, setServerError] = useState(false);
  const auth = useSelector((state) => state.auth);
  const [loading, setLoading] = useState(false);
  const intl = useIntl();
  const history = useHistory();
  const [filters, setFilters] = useState({
    branchCode: '',
    branchName: '',
  });

  console.log({ isDistributor });

  const getBranches = async () => {
    try {
      setLoading(true);

      setTableData([...branches].reverse());
      setServerError(false);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setServerError(true);
      toast('Something went wrong!');
    }
  };

  useEffect(() => {
    getBranches();
  }, [branches]);

  const renderImage = (value) => {
    return (
      <Center justifyContent='center'>
        <Image
          width='5vw'
          src={value.startsWith('https') ? value : garaazLogo}
          alt='part'
        />
      </Center>
    );
  };

  const columns = useMemo(
    () => [
      {
        Header: 'S. No',
        accessor: 'sno',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: 'Image',
        accessor: (row) =>
          row.gallery?.length > 0 ? row.gallery[0]?.path : 'thumbnail',
        Cell: ({ value }) => renderImage(value),
      },

      {
        Header: 'Branch Code',
        accessor: 'branchCode',
      },
      {
        Header: 'Branch Name',
        accessor: 'branchName',
      },
      {
        Header: 'Branch Address',
        accessor: 'location[0].addressLine1',
      },
      {
        Header: 'Mobile Number',
        accessor: 'contact[0].alternateNumbers',
      },
      {
        Header: 'Actions',
        accessor: (row) => row._id,
        Cell: (allData) => {
          return (
            <Menu>
              <MenuButton p={0} as={Button}>
                <Center>
                  <FiMoreVertical />
                </Center>
              </MenuButton>

              <MenuList>
                <MenuItem
                  onClick={() =>
                    history.push(`/branch/${allData.row.original._id}/edit`)
                  }
                >
                  Edit
                </MenuItem>
              </MenuList>
            </Menu>
          );
        },
      },
    ],
    [],
  );

  const isEmployeeCol = useMemo(
    () => [
      {
        Header: 'S. No',
        accessor: 'sno',
        Cell: (allData) => {
          return <Box>{allData?.row?.index + 1}</Box>;
        },
      },
      {
        Header: 'Image',
        accessor: (row) =>
          row.gallery?.length > 0 ? row.gallery[0]?.path : 'thumbnail',
        Cell: ({ value }) => renderImage(value),
      },

      {
        Header: 'Branch Code',
        accessor: 'branchCode',
      },
      {
        Header: 'Branch Name',
        accessor: 'branchName',
      },
      {
        Header: 'Branch Address',
        accessor: 'location[0].addressLine1',
      },
      {
        Header: 'Mobile Number',
        accessor: 'contact[0].alternateNumbers',
      },
    ],
    [],
  );

  const inputData = useMemo(
    () => ({
      title: 'Search',
      type: 'text',
      placeholder: '',
      name: 'branchSearch',
      width: '20%',
    }),

    [],
  );

  const handleTableTopChange = async (e, name) => {
    const value = e.hasOwnProperty('target') ? e.target.value : e;
    const newFilters = { ...filters, [name]: `${value}`.trim() };
    setFilters(newFilters);

    setTableData(
      branches.filter((branch) => {
        return (
          `${branch.branchCode}`
            .toLowerCase()
            .indexOf(newFilters.branchSearch.toLowerCase()) !== -1 ||
          `${branch.branchName}`
            .toLowerCase()
            .indexOf(newFilters.branchSearch.toLowerCase()) !== -1
        );
      }),
    );
  };

  const handleExport = () => {
    const refinedTableData = [...tableData].map((data) => {
      return {
        'Branch Name': data.branchName,
        'Branch Code': data.branchCode,
        'Branch Address': data.location[0].addressLine1,
        'Mobile Number': data.contact[0].alternateNumbers.join(','),
      };
    });
    downloadExcel(refinedTableData, 'Branches.xlsx');
  };

  return (
    <Box minHeight='68vh'>
      <Box>
        {branches.length > 0 && (
          <Box
            display='flex'
            alignItems='center'
            justifyContent='space-between'
          >
            <SingleInput inputObj={inputData} onChange={handleTableTopChange} />
            {isDistributor && (
              <Box>
                <Link to='/branch/add'>
                  <Button variant='addition' size='md'>
                    {intl.formatMessage(messages.addBranch)}
                  </Button>
                </Link>
                <Link to={ROUTES.BRANCH_ADD_BULK}>
                  <Button variant='addition' size='md' ml={4}>
                    {intl.formatMessage(messages.uploadBranches)}
                  </Button>
                </Link>
               {isDistributor && (<Button
                  onClick={handleExport}
                  variant='addition'
                  size='md'
                  ml={4}
                >
                  {intl.formatMessage(messages.exportToExcel)}
                </Button>)}
              </Box>
            )}
          </Box>
        )}
      </Box>
      <div className='flex-auto  pb-6 pt-0'>
        {serverError ? (
          <Error
            onRetry={getBranches}
            title='Server Error'
            message='Something went wrong, please try again!'
            illustrator={serverErrorIllustrator}
          />
        ) : branches.length < 1 ? (
          loading ? (
            <Loading />
          ) : (
            <AdditionOption {...optionsProp} />
          )
        ) : (
          <Box mt={5}>
            <DynamicTable
              columns={isDistributor ? columns : isEmployeeCol}
              data={tableData}
              fetchData={() => console.log('...fetching')}
              skipPageReset={true}
              //loading={tableLoading}
              hidePagination={true}
            />
          </Box>
        )}
      </div>
    </Box>
  );
};

export default BranchList;
