import React from 'react'
import PropTypes from 'prop-types';
import { useHistory } from 'react-router';
import nodataIllustrator from '../../assets/img/NoData.svg'
import {
  Table, Thead, Tbody, Tfoot, Tr, Th, Td, Flex, useDisclosure, Button,
} from "@chakra-ui/react"
import Empty from '../Empty';
const InviteList = ({ invites = [] }) => {
  const history = useHistory()
  return (
    invites.length > 0 ?
      <div style={{ width: "100%", overflow: "auto" }}>
        <Table variant="simple" style={{ width: "max-content", overflow: "auto" }}>
          <Thead>
            <Tr>
              <Th> Status</Th>
              <Th>Employee Name</Th>
              <Th>Mobile Number</Th>
              <Th >Active</Th>
            </Tr>
          </Thead>
          <Tbody>
            {invites.map((invite) => {
              const { firstName, lastName, mobileNumber, status, active } = invite
              return (
                <Tr key={invite.inviteId}>
                  <Td>{status}</Td>
                  <Td>{firstName} {lastName}</Td>
                  <Td>{mobileNumber}</Td>
                  <Td>{active}</Td>
                </Tr>
              )
            })}
          </Tbody>
          <Tfoot>
            <Tr>
              <Td>{invites.length} Employees</Td>
            </Tr>
          </Tfoot>
        </Table>
      </div> :
      <Empty title="Employee" message="No invites found, please add one" onAdd={() => { history.push('/employee/add') }} illustrator={nodataIllustrator} />

  );

}
InviteList.prototype = {
  invites: PropTypes.array,
};


export default InviteList;
