import { Button } from "@chakra-ui/button";
import { Box } from "@chakra-ui/layout";
import './style.css';

const ActionPop = ({ openActionPop, setOpenActionPop, position, onOpen }) => {
  return (
    openActionPop &&
    <Box className="popover" style={{
      backgroundColor: "#e2e8f0",
      borderRadius: "10px",
      width: "max-content",
      height: "15%",
      display: openActionPop ? "flex" : "none",
      justifyContent: "center",
      alignItems: "center",
      position: "absolute",
      right: position.right,
      top: position.top,
    }}>
      <Button
        onClick={() => {
          setOpenActionPop((prev) => !prev)
          onOpen()
        }}
        style={{
          margin: "10px",
          outline: "none"
        }} ><i className="fas fa-user-edit"></i></Button>
      <Button
        style={{
          margin: "10px",
          outline: "none",
          backgroundColor: "#da0032",
        }}
      ><i className="fas fa-trash" style={{ color: "#fff" }}></i></Button>
    </Box>
  );
}

export default ActionPop;
