import React from 'react';
import PropTypes from 'prop-types';
import { BrowserView, MobileView } from 'react-device-detect';
import { useHistory } from 'react-router-dom';
import { FormattedMessage } from 'react-intl';
import Navbar from '../../components/Navbar';
import PreFooter from '../../components/PreFooter';
import Footer from '../../components/Footer';
import LoginForm from '../../components/LoginForm';
import messages from './messages';
import withAuthentication from '../../utils/withoutAuth';


const Login = ({ color = 'dark' }) => {
  const history = useHistory();


  const panelColorClass = {
    light: 'bg-blueGray-200 text-blueGray-700',
    dark: 'bg-blueGray-800 text-white',
    blueGray: 'bg-blueGray-500 text-white',
    red: 'bg-red-500 text-white',
    orange: 'bg-orange-500 text-white',
    amber: 'bg-amber-500 text-white',
    emerald: 'bg-emerald-500 text-white',
    teal: 'bg-teal-500 text-white',
    lightBlue: 'bg-lightBlue-500 text-white',
    indigo: 'bg-indigo-500 text-white',
    purple: 'bg-purple-500 text-white',
    pink: 'bg-pink-500 text-white',
  };

  const redirectToRegisterPage = () => {
    history.push('/register');
  };

  return (
    <>

      <Navbar />
      <main style={{ height: "97%" }}>
        <section className="relative pt-24 pb-4 z-2 bg-blueGray-200" style={{ height: "100%" }}>
          <div className="container mx-auto px-4" style={{ height: "100%", alignItems: "center", display: "flex", justifyContent: "center" }}>
            <BrowserView>
              <div className="bg-blueGray-300 rounded-lg shadow-lg relative overflow-hidden h-full z-1 max-w-full min-h-480-px w-768-px container mx-auto px-4">
                <LoginForm isFull={false} />
              </div>
            </BrowserView>
            <MobileView>
              <div className="bg-blueGray-300 rounded-lg shadow-lg relative overflow-hidden h-full z-1 max-w-full min-h-480-px w-768-px container mx-auto px-4">
                <LoginForm isFull />
              </div>
            </MobileView>
          </div>
        </section>
      </main>
      {/* <PreFooter /> */}
      <Footer />
    </>
  );
};

Login.propTypes = {
  color: PropTypes.oneOf([
    'light',
    'dark',
    'blueGray',
    'red',
    'orange',
    'amber',
    'emerald',
    'teal',
    'lightBlue',
    'indigo',
    'purple',
    'pink',
  ]),
};

export default withAuthentication(Login);
