import { publicAxios as axios } from '../../axios';

export const getPart = async (auth, pId) => {
  try {
    const { data } = await axios.get(`/part/${pId}`, {
      headers: {
        Authorization: auth.authToken,
      },
    });
    return data.part;
  } catch (error) {
    return error;
  }
};
