import { Box, Text, } from "@chakra-ui/react";
import '../../utils/theme/Addition-Template.css'
import Option from "./Option";
const AdditionOption = (props) => {
  const { indivi, bulk } = props
  return (
    <div className="option-container" style={{ alignSelf: "center" }}>
      <div className="options-wrapper">
        <Option {...indivi} />
        <Box className="divider-box">
          <div className="divider"></div>
          <Text>or</Text>
          <div className="divider"></div>
        </Box>
        <Option {...bulk} />
      </div>
    </div>
  );
}
export default AdditionOption;
