import { defineMessages } from 'react-intl';

export const scope = 'app.components.PartDetailsForm';

export default defineMessages({
  partDetails: {
    id: `${scope}.partDetails`,
    defaultMessage: 'Part Details',
  },
  mrp: {
    id: `${scope}.mrp`,
    defaultMessage: 'MRP',
  },
  mrpRequired: {
    id: `${scope}.mrpRequired`,
    defaultMessage: 'MRP field is required.',
  },
  sellingPrice: {
    id: `${scope}.sellingPrice`,
    defaultMessage: 'Selling Price',
  },
  sellingPriceRequired: {
    id: `${scope}.sellingPriceRequired`,
    defaultMessage: 'Selling Price field is required.',
  },
  hsnCode: {
    id: `${scope}.hsnCode`,
    defaultMessage: 'HSN Code',
  },
  hsnCodeRequired: {
    id: `${scope}.hsnCodeRequired`,
    defaultMessage: 'HSN Code field is required.',
  },
  taxSlab: {
    id: `${scope}.taxSlab`,
    defaultMessage: 'Tax Slab',
  },
  taxSlabRequired: {
    id: `${scope}.taxSlabRequired`,
    defaultMessage: 'Tax Slab field is required.',
  },

  description: {
    id: `${scope}.description`,
    defaultMessage: 'Description',
  },
  descriptionRequired: {
    id: `${scope}.descriptionRequired`,
    defaultMessage: 'Description field is required.',
  },
  partMedia: {
    id: `${scope}.partMedia`,
    defaultMessage: 'Part Media',
  },
  partMediaRequired: {
    id: `${scope}.partMediaRequired`,
    defaultMessage: 'Part Media field is required.',
  },
  saveButtonLabel: {
    id: `${scope}.saveButtonLabel`,
    defaultMessage: 'Submit',
  },
  cancelButtonLabe: {
    id: `${scope}.cancelButtonLabe`,
    defaultMessage: 'Clear',
  },
  dragAndDropMessage: {
    id: `${scope}.dragAndDropMessage`,
    defaultMessage: 'Drag and drop a file here, or click to select file.',
  },
  dropFilesHere: {
    id: `${scope}.dropFilesHere`,
    defaultMessage: 'Drop your files here...',
  },
  partDetailsSaveSuccess: {
    id: `${scope}.partDetailsSaveSuccess: {
      `,
    defaultMessage: 'Part Details Save Success',
  },
  somethingWentWrong: {
    id: `${scope}.somethingWentWrong: {
      `,
    defaultMessage: 'Something Went Wrong!',
  },
  dropFileHere: {
    id: `${scope}.dropFileHere: {
      `,
    defaultMessage: 'Drop files Here',
  },
  selectTaxSlab: {
    id: `${scope}.selectTaxSlab: {
      `,
    defaultMessage: 'Select Tax Slab',
  },
  partUpdateSuccess: {
    id: `${scope}.partUpdateSuccess: {
      `,
    defaultMessage: 'Part details Updated Successfully',
  },
  partAddedSuccess: {
    id: `${scope}.partAddedSuccess: {
      `,
    defaultMessage: 'Part details added successfully',
  },
});
