import routes from './routes';
import getURLParams from '../../utils/getURLParams';
import { publicAxios as axios } from '../../axios';



class Services {
    static fetchAllOutstandingAcct = (payload) => {
        const params = getURLParams(payload.params)
        return axios({
            method: 'GET',
            url: `${routes.outstandingRoute}${params}`,
            headers: {
                Authorization: payload.auth.authToken,
            }
        })
    }

    static fetchWorkshopAccounts = (payload) => {
        const params = getURLParams(payload.params)
        return axios({
            method: 'GET',
            url: `${routes.workshopRoute}/${payload.id}/outstandings`,
            headers: {
                Authorization: payload.auth.authToken,
            }
        })
    }
}





export default Services
