import React, { lazy, Suspense, useState } from 'react';
import { useIntl } from 'react-intl';
import {
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Box,
  Center,
  Button,
} from '@chakra-ui/react';
import Layout from '../Layout';
import messages from './messages';
import Loader from './Loader';
import Invites from '../../components/Invites';
import '../../assets/customStyle.css';
import './style.css';
import '../../utils/theme/Addition-Template.css';
import { useHistory } from 'react-router';
import { UseOnboardContext } from '../../App';

import { set } from 'lodash';
const WorkshopList = lazy(() => import('../../components/WorkShopList'));

const Workshop = () => {
  const history = useHistory();
  const intl = useIntl();
  const { onboard } = UseOnboardContext();
  
  if (onboard && onboard < 4) {
    return (
      <Center minHeight='80vh' flexDirection='column'>
        <Box mb={4} textStyle='medium' display='block'>
          Please complete employee onboarding
        </Box>
        <Button onClick={() => history.push('/employees')} variant='normal'>
          {' '}
          Go to employee
        </Button>
      </Center>
    );
  }

  return (
    <Box minHeight='81vh'>
      <div className='w-full mb-3'>
        <Suspense fallback={<Loader />}>
          <WorkshopList />
        </Suspense>
      </div>
    </Box>
  );
};

export default Workshop;
