import React from "react";
import Layout from '../Layout';
import EmployeeAdd from "../../components/EmployeeAddition";
import { Button, useDisclosure } from "@chakra-ui/react";
import { useHistory } from "react-router";
import EmployeeRoles from "./EmployeeRoles";
import messages from "./messages";
import { useIntl } from "react-intl";
const EmployeeAddition = () => {
  const intl = useIntl()
  const history = useHistory();
  const { isOpen: isRolesOpen, onOpen: onRolesOpen, onClose: onRolesClose } = useDisclosure()
  return (
    <>
        <div className="mb-0 p-6 pb-0">
          <div className="text-center flex">
            <div style={{ alignSelf: "flex-end", marginLeft: "auto" }}>
              <Button
                size="md"
                onClick={onRolesOpen}
                style={{ marginBottom: "10px", marginRight: "10px" }}
              > 
              {intl.formatMessage(messages.employeeRole)}&nbsp; <i className="far fa-question-circle"></i>
              </Button>

              <Button
                variant="bulkAdd"
                onClick={() => {
                  history.push('/employee/add/bulk')
                }}
              >
                Bulk Upload
              </Button>
            </div>
          </div>
        </div>
        <div className="flex-auto px-6 pb-6 pt-0">
          <EmployeeAdd />
        </div>
      <EmployeeRoles isOpen={isRolesOpen} onClose={onRolesClose} />
    </>
  );
}
export default EmployeeAddition
