import _, { capitalize, upperCase } from 'lodash';
import { getBrands, getCategories } from '../../containers/PartAddition/action';
import { requiredFields } from './constants';


export const sheetValidation = async (sheetData, data, acceptedPartsOnboardingHeaders) => {

  const [brands, categories] = await Promise.all([await getBrands(), await getCategories()])
  const brandsName = brands.map(brand => brand.name)
  //const categoriesName = categories.map(cate => cate.category)

  const taxSlab = [5, 12, 18, 28];
  const optionNumbersFields = ["Selling Price", "Pack Quantity", "Pack Size"]
  const mandatoryNumberFields = ["MRP", "HSN Code"]
  // const defaultCategory = await categories.filter(cat => cat.category === "All Parts");

  const missingFields = {singleField: [], other: []};
    const inValidData = [];
    const validData = [];

    sheetData.forEach(async (row) => {
    const mappedRow = {};
    let isInvalid =  false;
    acceptedPartsOnboardingHeaders.forEach((header) => {
      if (requiredFields.includes(header) && row[data[header]] === '') {
        isInvalid = true;
        missingFields.singleField.push(header);
      }else if (header === "Tax Slab" && !taxSlab.includes(row[data[header]])) {
        mappedRow.taxSlab = 'invalid';
        isInvalid = true;
        missingFields.other.push(`${header} must be 5, 12, 18 or 28`);
      }
      else if (mandatoryNumberFields.includes(header) && typeof (row[data[header]]) !== "number") {
        mappedRow[_.camelCase(header)] = "invalid";
        isInvalid = true;
        missingFields.other.push(`${header} must be numbers`);
      } else if (optionNumbersFields.includes(header) && row[data[header]] !== "" && typeof row[data[header]] !== "number") {
        mappedRow[_.camelCase(header)] = "invalid";
        isInvalid = true;
        missingFields.other.push(`${header} must be numbers`);
      }
      else if (header === "Brand") {
        if (brandsName.includes(row[data[header]])) {
          const brnd = brands.filter(brand => brand.name === row[data[header]]);
          mappedRow.manufacturerId = brnd[0]._id;
          mappedRow.brand = row[data[header]]
        } else {
          mappedRow.manufacturerId = "invalid";
          mappedRow.brand = "invalid";
         isInvalid = true;
        }
      } /* else if (header === "Part Category" && row[data[header]]) {
        if (categoriesName.includes(row[data[header]])) {
          const cate = categories.filter(cat => cat.category === row[data[header]]);
          mappedRow.categoryId = cate[0]._id;
        } else {
          mappedRow.categoryId = defaultCategory[0]?._id || "invalid"
          mappedRow.category = defaultCategory[0]?._id || "invalid"
          isInvalid = true
        }
      } */ else if(header === 'Origin'){
        mappedRow[_.camelCase(header)] = upperCase(row[data[header]]);
      }else {
        mappedRow[_.camelCase(header)] = row[data[header]];
      }

     
    });
  
   /*  return {data:mappedRow, missingFields} */

   if(isInvalid){
    inValidData.push({ data: mappedRow, missingFields })
  }else {
    mappedRow.partType = capitalize(mappedRow.partType)
    validData.push({ data: mappedRow, missingFields })
  }
  isInvalid= false;

  })

  return { inValidData, validData };
}
