import React from 'react';
import { Box, Badge, Image, Stack, Button, IconButton } from '@chakra-ui/react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FiEdit3 } from 'react-icons/fi';

const convertTime = (time) => {
  var H = +time.substr(0, 2);
  var h = H % 12 || 12;
  var ampm = H < 12 || H === 24 ? 'AM' : 'PM';
  return h + time.substr(2, 3) + ampm;
};

const BranchItemCard = ({ branchInfo = {} }) => {
  const {
    branchName = '',
    branachRo = '',
    contact = [],
    gallery = [],
    thumbnail = '',
    inchargeCode = '',
    startTime = '',
    endTime = '',
    location = [],
  } = branchInfo;
  const StartTime = convertTime(startTime);
  const EndTime = convertTime(endTime);
  const imgSource = gallery[0]?.path || thumbnail;

  return (
    <StyledCard
      //margin={3}
      borderWidth='1px'
      borderRadius='lg'
      overflow='hidden'
      minHeight='35%'
      width={{base:'100%', lg:'32%'}}
      display='flex'
      alignItems='center'
      justifyContent='center'
      p={2}

    >
    
        <Box width='35%'>
          <Image  objectFit='cover' src={imgSource} alt='image' />
        </Box>
  
        <Box p='6'>
          <Box
            mt='1'
            fontWeight='semibold'
            as='h4'
            lineHeight='tight'
            isTruncated
          >
            {branchName}
          </Box>

          <Box>{inchargeCode}</Box>
          <Box>{branachRo}</Box>
          <Box>{contact.length > 0 ? contact[0].landlineNumbers[0] : ''}</Box>
          <Box>
            {location.length > 0
              ? `${location[0].city}  ${location[0].state}`
              : ''}
          </Box>
          <Box d='flex' mt='2' alignItems='center'>
            <Box textStyle='labelLight'>
              {StartTime} to {EndTime}
            </Box>
          </Box>
          <IconButton>
          <Link to={`/branch/${branchInfo._id}/edit`}>

            <FiEdit3/>
          </Link>

          </IconButton>
          {/* <Box as='span' mt='5' color='gray.600' fontSize='sm'>
           
              <Button style={{ backgroundColor: 'teal', color: '#ffffff' }}>
                Edit Branch
              </Button>
            </Link>
          </Box> */}
        </Box>

    </StyledCard>
  );
};

const StyledCard = styled(Box)`
  margin-right: 2%;
  margin-bottom: 15px;

  &:nth-child(3n+3){
    margin-right: 0%;
  }
`

export default BranchItemCard;
