import toast from 'react-hot-toast';
import { publicAxios as axios } from '../../axios'

export const getBranchInfo = async (auth, branchid) => {
  try {
    const { data } = await axios.get(`/branches/${branchid}`, {
      headers: {
        Authorization: auth.authToken,
      },
    });
    return data[0] ? data[0] : {}
  } catch (error) {
    toast("Something went wrong, while getting branch details!")
  }
}
