import React, { useState, useMemo, useEffect } from 'react';
import { useIntl } from 'react-intl';

import messages from './messages';
import {
  Badge,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Button,
  Center,
} from '@chakra-ui/react';
import TableTop from '../../components/TableTop';
import { useSelector, useDispatch } from 'react-redux';
import { getAllOrders, setAllOrders, setTotalCount } from './actions';
import moment from 'moment';
import toast from 'react-hot-toast';
import DynamicTable from '../../components/DynamicTable';
import { setBranches, getBranchList } from '../BranchList/actions';
import enums from '../../utils/enums';
import { FiMoreVertical } from 'react-icons/fi';
import OrderDetails from '../OrderDetails';
import { Switch, Route, useRouteMatch, useHistory } from 'react-router-dom';
import stringifyArray from '../../utils/stringifyArray';
import withToast from '../../HOCs/withToast';
import OrderOverview from '../OrderDetails/OrderOverView';
import RequestOrder from '../OrderDetails/RequestOrder';
import getMoneyFormat from '../../utils/getMoneyFormat';
import {selectGlobalState} from '../../globalSlice'; 


const OrderList = ({ notification }) => {
  const intl = useIntl();
  const [orderStatus, setOrderStatus] = useState([]);
  const [branchValue, setBranchValue] = useState([]);
  const [workshopValue, setWorkshopValue] = useState([]);
  const [dates, setDates] = useState({ startDate: '', endDate: '' });
  const [orderIds, setOrdIds] = useState('');
  const auth = useSelector((state) => state.auth);
  const { orders, totalCount } = useSelector((state) => state.orderList);
  const dispatcher = useDispatch();
  const [isLoading, setIsLoading] = useState(false);
  const { url, path } = useRouteMatch();
  const history = useHistory();
  const [pageCount, setPageCount] = useState(0);
  const { branches, workshops } = useSelector(selectGlobalState);



  const fetchAllOrders = (params) => {
    (async () => {
      setIsLoading(true);
      try {
        const response = await getAllOrders(auth, params);

        dispatcher(setAllOrders(response?.data));

       params.limit = params?.limit? params?.limit : 10;
        setPageCount(Math.ceil(response?.totalCount?.count / params?.limit));

        dispatcher(setTotalCount(response?.totalCount?.count));
        setIsLoading(false);
      } catch (error) {
        setIsLoading(false);
        if (error.response) {
          notification({
            status: 'error',
            description:
              error.response.data.message ??
              intl.formatMessage(messages.errorMessage),
          });
        }
      }
    })();
  };

  useEffect(() => {
    fetchAllOrders();
  }, []);

  const columns = useMemo(
    () => [
      {
        Header: intl.formatMessage(messages.date),
        accessor: 'createdAt',
        Cell: ({ value }) => moment(value).format('DD/MM/YYYY'),
      },
      {
        Header: intl.formatMessage(messages.orderNo),
        accessor: 'orderNo',
        Cell: ({ value }) => value,
      },
      {
        Header: intl.formatMessage(messages.workshopCode),
        accessor: (row) => row.workshop?.workshopcustomer[0]?.code,
        //Cell: ({ value }) => value?.slice(0, 8),
      },
      {
        Header: intl.formatMessage(messages.workshopName),
        accessor: (row) => row.workshop?.workshopcustomer[0]?.name,
      },
      {
        Header: intl.formatMessage(messages.branchCode),
        accessor: (row) => row.branch?.branchCode,
      },
      {
        Header: intl.formatMessage(messages.totalParts),
        accessor: (row) =>
          row.orderitems.length
      },
      {
        Header: intl.formatMessage(messages.orderValue),
        accessor: (row) =>
         /*  row.orderitems.reduce(
            (prevState, currentVal) => prevState + currentVal.price,
            0,
          ), */


           row.orderitems.reduce(
            (prev, curr) => prev + curr.price * curr.quantity,
            0,
          ),
          Cell: ({ value }) => getMoneyFormat(value),

      },
      {
        Header: intl.formatMessage(messages.paymentMode),
        accessor: (row) => row.paymentOption,
      },
      {
        Header: intl.formatMessage(messages.orderStatus),
        accessor: 'status',
        Cell: ({ value }) => {
          return value === enums.UPDATED ? (
            <Badge colorScheme='yellow'>
              {intl.formatMessage(messages.updatedStatus)}
            </Badge>
          ) : value === enums.COMPLETED ? (
            <Badge colorScheme='green'>
              {intl.formatMessage(messages.completedStatus)}
            </Badge>
          ) : (
            <Badge colorScheme='orange'>{value.replace(/_/g, ' ')}</Badge>
          );
        },
      },
      {
        Header: intl.formatMessage(messages.actions),
        accessor: (row) => row._id,
        Cell: (allData) => {
          const handleViewOrderDetails = () => {
            const status = allData.row.original.status;
            if (
              status === enums.COMPLETED ||
              status === enums.STOCK_TRANSFER_COMPLETED ||
              status === enums.STOCK_TRANSFER_PLACED ||
              status === enums.CBO ||
              status === enums.LOST_SALE
            ) {
              history.push(`${url}/${allData.value}/overview`);
            } else if (status === enums.REQUEST_ORDER) {
              history.push(`${url}/${allData.value}/request-order`);
            } else if (status === enums.UPDATED) {
              history.push(`${url}/${allData.value}/order-summary`);
              /* history.push(`${url}/${allData.value}/request-order`); */
            } else {
              history.push(`${url}/${allData.value}`);
            }
          };

          return (
            <Menu>
              <MenuButton p={0} as={Button}>
                <Center>
                  <FiMoreVertical />
                </Center>
              </MenuButton>

              <MenuList onClick={handleViewOrderDetails}>
                <MenuItem>View Detail</MenuItem>
              </MenuList>
            </Menu>
          );
        },
      },
    ],
    [],
  );

  const handleSelectChange = (valueList, name) => {
    const valueString = stringifyArray(valueList);
    const statusString = stringifyArray(orderStatus);
    const workshopString = stringifyArray(workshopValue);
    const branchString = stringifyArray(branchValue);
    const dateString = stringifyArray([dates.startDate, dates.endDate]);

    if (name === enums.STATUS) {
      setOrderStatus(valueList);
      fetchAllOrders({
        status: valueString,
        order: orderIds,
        workshop: workshopString,
        branch: branchString,
        date: dateString,

      });
    } else if (name === enums.WORKSHOP) {
      setWorkshopValue(valueList);
      fetchAllOrders({
        workshop: valueString,
        order: orderIds,
        branch: branchString,
        status: statusString,
        date: dateString,

      });
    } else if (name === enums.BRANCH) {
      setBranchValue(valueList);
      fetchAllOrders({
        branch: valueString,
        order: orderIds,
        workshop: workshopString,
        status: statusString,
        date: dateString,

      });
    }
  };

  const handleSearchById = (event) => {
    const idString = event.target.value;
    const statusString = stringifyArray(orderStatus);
    const workshopString = stringifyArray(workshopValue);
    const branchString = stringifyArray(branchValue);
    const dateString = stringifyArray([dates.startDate, dates.endDate]);

    setOrdIds(idString);
    fetchAllOrders({
      status: statusString,
      order: idString,
      workshop: workshopString,
      branch: branchString,
      date: dateString,

    });
  };

  /* once workshop is implemented, this list will be replaced with fetched workshops */
  const workshopOptions = workshops.map((workshop) => ({
    label: workshop?.name,
    value: workshop?._id,
  }));

  const branchOptions = branches.map((branch) => ({
    label: branch.branchName,
    value: branch._id,
  }));

  const checkIsBefore = (date1, date2) => {
    return moment(date1).isBefore(date2);
  };

  const addToDate = (date, days) => {
    return moment(date, 'YYYY-MM-DD').add(days, 'days').format('YYYY-MM-DD');
  };

  function subtractFromDate(date, days) {
    return moment(date, 'YYYY-MM-DD')
      .subtract(days, 'days')
      .format('YYYY-MM-DD');
  }

  const handleDateChange = (event) => {
    let dateString;

    let { value, name } = event.target;

    const statusString = stringifyArray(orderStatus);
    const workshopString = stringifyArray(workshopValue);
    const branchString = stringifyArray(branchValue);

    if (name === enums.STARTDATE && value) {
      const endDate = dates.endDate ? dates.endDate : addToDate(value, 1);

      if (!checkIsBefore(value, endDate)) {
        notification({
          status: 'error',
          description: intl.formatMessage(messages.dateError),
        });
        return;
      } else {
        dateString = stringifyArray([value, endDate]);
      }
    }

    if (name === enums.ENDDATE && value) {
      const startDate = dates.startDate
        ? dates.startDate
        : subtractFromDate(value, 1);

      if (checkIsBefore(value, startDate)) {
        notification({
          status: 'error',
          description: intl.formatMessage(messages.dateError),
        });
      } else {
        dateString = stringifyArray([startDate, value]);
      }
    }

    setDates((prevState) => ({ ...prevState, [name]: value }));
    fetchAllOrders({
      date: dateString,
      status: statusString,
      order: orderIds,
      workshop: workshopString,
      branch: branchString,
    });
  };

  return (
    <Switch>
      <Route exact path={path}>
        <div>
          <TableTop
            onSearchOrderChange={handleSearchById}
            onSelectChange={handleSelectChange}
            orderStatus={orderStatus}
            workshopOptions={workshopOptions}
            branchOptions={branchOptions}
            branchValue={branchValue}
            workshopValue={workshopValue}
            onDateChange={handleDateChange}
          />

          <DynamicTable
            columns={columns}
            data={[...orders]}
            fetchData={fetchAllOrders}
            totalCount={totalCount}
            controlledPageCount={pageCount}
            loading={isLoading}
            skipPageReset={true}
            params={{ order: orderIds, status: stringifyArray(orderStatus), branch: stringifyArray(branchValue),workshop:stringifyArray(workshopValue), date:stringifyArray([dates.startDate, dates.endDate])   }}
          />
        </div>
      </Route>
      <Route exact path={`${path}/:id/overview`}>
        <OrderOverview />
      </Route>
      <Route exact path={`${path}/:id/request-order`}>
        <RequestOrder />
      </Route>
      <Route path={`${path}/:id`}>
        <OrderDetails />
      </Route>
    </Switch>
  );
};

export default withToast(OrderList);
