export const getCamelCase = (word) => {
  return word
    .split(' ')
    .map((elem, index) => {
      if (index === 0) return elem.toLowerCase();
      else {
        let newEl = elem.toLowerCase();
        return newEl.replace(newEl.charAt(0), newEl[0].toUpperCase());
      }
    })
    .join('');
};
