/*eslint-disable*/
import React from 'react';
//import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { FormattedMessage } from 'react-intl';
import messages from './messages';
import { useIntl } from 'react-intl';

const links = [
  {
    children: <FormattedMessage {...messages.garaazLink} />,
    href: 'https://garaaz.com/',
    target: '_blank',
  },
  {
    children: <FormattedMessage {...messages.aboutUsLink} />,
    href: 'https://garaaz.com/',
    target: '_blank',
  },
  {
    children: <FormattedMessage {...messages.blogLink} />,
    href: 'https://garaaz.com/',
    target: '_blank',
  },
];

const Footer = ({ type = 'light' }) => {
  let copyrightClass = 'text-center xl:text-left py-0 text-sm';
  const wrapperClasses = {
    dark: 'bg-blueGray-800',
    light: 'bg-blueGray-100',
  };
  const copyrightClasses = {
    dark: 'text-white',
    light: 'text-blueGray-500',
  };
  const intl = useIntl();
  return (
    <>
      <footer className={wrapperClasses[type]}>
        <div className="container mx-auto px-4">
          <div className="items-center xl:justify-between flex flex-wrap -mx-4">
            <div className="px-4 relative w-full xl:w-6/12 w-full sm:w-full">
              <div className={copyrightClass + ' ' + copyrightClasses[type]}>
                {/* <FormattedMessage {...messages.copyright} /> © 2021 */}
                {/* <FormattedMessage {...messages.allRightsReserved} /> */}
                <p>{intl.formatMessage(messages.copyright)} © 2021. {intl.formatMessage(messages.AllRightsReserved)} </p>
              </div>
            </div>

            <div className="px-4 relative w-full xl:w-6/12 w-full sm:w-full">
              <ul className="justify-center xl:justify-end mx-auto flex flex-wrap list-none pl-0 mb-0">
                {links.map((prop, key) => {
                  return (
                    <li key={key}>
                      <a
                        {...prop}
                        className="text-sm block px-4 bg-transparent no-underline text-blueGray-500 hover:text-blueGray-700 md:py-0 mx-auto"
                      />
                    </li>
                  );
                })}
              </ul>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
};

Footer.propTypes = {
  type: PropTypes.oneOf(['light', 'dark']),
};

export default Footer;
